import express, { type Request, Response, NextFunction } from "express";
import helmet from "helmet";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();

// Security middleware with Helmet
app.use(helmet({
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
      fontSrc: ["'self'", "https://fonts.gstatic.com"],
      imgSrc: ["'self'", "data:", "https:", "blob:"],
      scriptSrc: ["'self'", "'unsafe-inline'", "'unsafe-eval'"], // unsafe-eval needed for dev
      connectSrc: ["'self'", "ws:", "wss:"],
      mediaSrc: ["'self'", "https:", "blob:"],
      objectSrc: ["'none'"],
      baseUri: ["'self'"],
      formAction: ["'self'"],
    },
  },
  hsts: {
    maxAge: 31536000, // 1 year
    includeSubDomains: true,
    preload: true,
  },
  noSniff: true,
  frameguard: { action: 'deny' },
  referrerPolicy: { policy: 'strict-origin-when-cross-origin' },
}));

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";
import { v2 as cloudinary } from "cloudinary";
import { storage } from "./storage";
import { authStorage } from "./auth-storage";
import { 
  insertUserSchema, 
  insertPostSchema, 
  insertPostCommentSchema, 
  insertStudentFollowerSchema,
  insertSchoolApplicationSchema,
  insertSystemSettingSchema,
  insertAdminRoleSchema,
  insertAnalyticsLogSchema,
  insertStudentSchema,
  insertStudentRatingSchema,
  insertSchoolSettingSchema
} from "@shared/schema";

// Configure Cloudinary
cloudinary.config({
  cloud_name: process.env.CLOUDINARY_CLOUD_NAME,
  api_key: process.env.CLOUDINARY_API_KEY,
  api_secret: process.env.CLOUDINARY_API_SECRET,
});

// Configure multer for file uploads
const upload = multer({ 
  storage: multer.memoryStorage(),
  limits: { fileSize: 50 * 1024 * 1024 }, // 50MB limit
});

// JWT secret
const JWT_SECRET = process.env.JWT_SECRET || 'dev-secret-key-change-in-production';

// Auth middleware
const requireAuth = (req: any, res: any, next: any) => {
  const token = req.headers.authorization?.replace('Bearer ', '');

  if (!token) {
    return res.status(401).json({ message: 'Authentication required' });
  }

  try {
    const decoded = jwt.verify(token, JWT_SECRET) as any;
    req.user = decoded;
    next();
  } catch (error) {
    return res.status(401).json({ message: 'Invalid token' });
  }
};

// Middleware to check if user can access their own data
const requireSelfAccess = (req: any, res: any, next: any) => {
  if (req.user.id !== req.params.userId) {
    return res.status(403).json({ message: 'Access denied' });
  }
  next();
};

// Middleware to check user role
const requireRole = (allowedRoles: string[]) => {
  return (req: any, res: any, next: any) => {
    if (!allowedRoles.includes(req.user.role)) {
      return res.status(403).json({ message: 'Insufficient permissions' });
    }
    next();
  };
};

export async function registerRoutes(app: Express): Promise<Server> {

  // Authentication routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ message: "Email and password required" });
      }

      const result = await authStorage.verifyPassword(email, password);

      if (!result) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const { user, profile } = result;

      // Generate JWT token
      const token = jwt.sign(
        { id: user.id, email: user.email, role: user.role, linkedId: user.linkedId },
        JWT_SECRET,
        { expiresIn: '7d' }
      );

      // Return user and profile data separately to avoid ID collision
      res.json({ 
        token,
        user: { 
          id: user.id, 
          email: user.email, 
          role: user.role,
          linkedId: user.linkedId
        },
        profile
      });
    } catch (error) {
      console.error('Login error:', error);
      res.status(500).json({ message: "Login failed" });
    }
  });

  app.post("/api/auth/register", async (req, res) => {
    try {
      const { email, password, role, ...profileData } = req.body;

      if (!email || !password || !role) {
        return res.status(400).json({ message: "Email, password, and role are required" });
      }

      const existingUser = await authStorage.getUserByEmail(email);

      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      const { user, profile } = await authStorage.createUserWithProfile(email, password, role, profileData);

      // Generate JWT token
      const token = jwt.sign(
        { id: user.id, email: user.email, role: user.role, linkedId: user.linkedId },
        JWT_SECRET,
        { expiresIn: '7d' }
      );

      // Return user and profile data separately to avoid ID collision
      res.json({ 
        token,
        user: { 
          id: user.id, 
          email: user.email, 
          role: user.role,
          linkedId: user.linkedId
        },
        profile
      });
    } catch (error) {
      res.status(400).json({ message: "Registration failed" });
    }
  });

  // User routes
  app.get("/api/users/me/:userId", requireAuth, requireSelfAccess, async (req, res) => {
    try {
      const { userId } = req.params;
      const profile = await authStorage.getUserProfile(userId);

      if (!profile) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json(profile);
    } catch (error) {
      console.error('Get user profile error:', error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User profile update (JSON) - for regular profile updates without files
  app.put("/api/users/:userId/profile", requireAuth, requireSelfAccess, async (req, res) => {
    try {
      const { userId } = req.params;
      const { role, ...updateData } = req.body;

      // Get current user to determine role
      const currentProfile = await authStorage.getUserProfile(userId);
      if (!currentProfile) {
        return res.status(404).json({ message: "User not found" });
      }

      const updatedProfile = await authStorage.updateUserProfile(userId, currentProfile.role, updateData);

      if (!updatedProfile) {
        return res.status(404).json({ message: "Failed to update profile" });
      }

      res.json(updatedProfile);
    } catch (error) {
      console.error('Update user profile error:', error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  // User profile picture update (FormData) - for file uploads
  app.put("/api/users/:userId", requireAuth, requireSelfAccess, upload.single("profilePic"), async (req, res) => {
    try {
      const { userId } = req.params;
      let updateData = { ...req.body };

      // Handle profile picture upload to Cloudinary for users
      if (req.file) {
        const result = await new Promise((resolve, reject) => {
          cloudinary.uploader.upload_stream(
            { 
              resource_type: "image",
              folder: "user-profiles",
              transformation: [
                { width: 400, height: 400, crop: "fill", gravity: "face" }
              ]
            },
            (error, result) => {
              if (error) reject(error);
              else resolve(result);
            }
          ).end(req.file!.buffer);
        });

        updateData.profilePicUrl = (result as any).secure_url;
      }

      const updatedUser = await storage.updateUser(userId, updateData);

      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({
        id: updatedUser.id,
        name: updatedUser.name,
        email: updatedUser.email,
        role: updatedUser.role,
        schoolId: updatedUser.schoolId,
        profilePicUrl: updatedUser.profilePicUrl
      });
    } catch (error) {
      console.error('Update user profile error:', error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  // Get all comments for a post
  app.get("/api/posts/:postId/comments", async (req, res) => {
    try {
      const { postId } = req.params;
      const comments = await storage.getPostComments(postId);
      res.json(comments);
    } catch (error) {
      console.error('Get post comments error:', error);
      res.status(500).json({ message: "Failed to fetch comments" });
    }
  });

  // Password change endpoint
  app.post("/api/users/:userId/change-password", requireAuth, requireSelfAccess, async (req, res) => {
    try {
      const { userId } = req.params;
      const { currentPassword, newPassword } = req.body;

      if (!currentPassword || !newPassword) {
        return res.status(400).json({ message: "Both current and new password are required" });
      }

      if (newPassword.length < 6) {
        return res.status(400).json({ message: "New password must be at least 6 characters long" });
      }

      // Verify current password using centralized auth
      const isCurrentPasswordValid = await authStorage.verifyCurrentPassword(userId, currentPassword);
      if (!isCurrentPasswordValid) {
        return res.status(400).json({ message: "Current password is incorrect" });
      }

      // Use centralized auth to change password securely
      await authStorage.changePassword(userId, newPassword);

      res.json({ message: "Password updated successfully" });
    } catch (error) {
      console.error('Change password error:', error);
      res.status(500).json({ message: "Failed to change password" });
    }
  });

  // Student routes
  app.get("/api/students/profile/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const studentWithStats = await storage.getStudentWithStats(userId);

      if (!studentWithStats) {
        return res.status(404).json({ message: "Student not found" });
      }

      res.json(studentWithStats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch student profile" });
    }
  });

  app.put("/api/students/profile/:userId", requireAuth, requireSelfAccess, upload.single("profilePic"), async (req, res) => {
    try {
      const { userId } = req.params;
      const student = await storage.getStudentByUserId(userId);

      if (!student) {
        return res.status(404).json({ message: "Student not found" });
      }

      let updateData = { ...req.body };

      // Handle profile picture upload to Cloudinary
      if (req.file) {
        const result = await new Promise((resolve, reject) => {
          cloudinary.uploader.upload_stream(
            { 
              resource_type: "image",
              folder: "student-profiles",
              transformation: [
                { width: 400, height: 400, crop: "fill", gravity: "face" }
              ]
            },
            (error, result) => {
              if (error) reject(error);
              else resolve(result);
            }
          ).end(req.file!.buffer);
        });

        updateData.profilePicUrl = (result as any).secure_url;
      }

      const updatedStudent = await storage.updateStudent(student.id, updateData);
      res.json(updatedStudent);
    } catch (error) {
      console.error('Update student profile error:', error);
      res.status(500).json({ message: "Failed to update student profile" });
    }
  });

  // Post routes
  app.get("/api/posts", async (req, res) => {
    try {
      const posts = await storage.getPosts();
      res.json(posts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch posts" });
    }
  });

  app.get("/api/posts/student/:studentId", async (req, res) => {
    try {
      const { studentId } = req.params;
      const posts = await storage.getPostsByStudent(studentId);
      res.json(posts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch student posts" });
    }
  });

  app.post("/api/posts/create", requireAuth, requireRole(['student']), upload.single('mediaFile'), async (req, res) => {
    try {
      // Derive studentId from authenticated user's linkedId with fallback
      const studentId = req.user.linkedId ?? (await authStorage.getUserProfile(req.user.id))?.linkedId;
      if (!studentId) {
        console.error('Student ID missing for user:', req.user.id, 'role:', req.user.role);
        return res.status(403).json({ message: "Student identity missing; please re-login" });
      }

      let contentUrl = '';
      let contentType = 'text';

      // Upload file to Cloudinary if provided
      if (req.file) {
        console.log('Uploading file to Cloudinary:', req.file.originalname);

        const result = await new Promise((resolve, reject) => {
          cloudinary.uploader.upload_stream(
            {
              folder: "lockerroom/posts",
              transformation: [
                { width: 1200, crop: "scale" },
                { quality: "auto:good" },
                { fetch_format: "auto" }
              ],
              resource_type: "auto"
            },
            (error, result) => {
              if (error) {
                console.error('Cloudinary upload error:', error);
                reject(error);
              } else {
                resolve(result);
              }
            }
          ).end(req.file.buffer);
        });

        contentUrl = (result as any).secure_url;
        contentType = (result as any).resource_type === 'video' ? 'video' : 'image';
        console.log('File uploaded successfully:', contentUrl, 'type:', contentType);
      }

      const postData = insertPostSchema.parse({ 
        studentId,
        caption: req.body.caption || '',
        mediaUrl: contentUrl,
        mediaType: contentType
      });

      const post = await storage.createPost(postData);
      console.log('Post created successfully:', post.id);
      res.json(post);
    } catch (error) {
      console.error('Create post error:', error);
      res.status(400).json({ message: "Failed to create post" });
    }
  });

  // Interaction routes
  app.post("/api/posts/:postId/like", requireAuth, async (req, res) => {
    try {
      const { postId } = req.params;
      const userId = req.user.id; // Use authenticated user ID

      const like = await storage.likePost({ postId, userId });
      res.json(like);
    } catch (error) {
      res.status(400).json({ message: "Failed to like post" });
    }
  });

  app.delete("/api/posts/:postId/like", requireAuth, async (req, res) => {
    try {
      const { postId } = req.params;
      const userId = req.user.id; // Use authenticated user ID

      await storage.unlikePost(postId, userId);
      res.json({ message: "Post unliked" });
    } catch (error) {
      res.status(400).json({ message: "Failed to unlike post" });
    }
  });

  // GET comments for a post
  app.get("/api/posts/:postId/comments", async (req, res) => {
    try {
      const { postId } = req.params;
      const limit = req.query.limit === 'all' ? undefined : 20;
      const comments = await storage.getPostComments(postId, limit);
      res.json(comments);
    } catch (error) {
      console.error('Get post comments error:', error);
      res.status(500).json({ message: "Failed to fetch comments" });
    }
  });

  app.post("/api/posts/:postId/comments", requireAuth, async (req, res) => {
    try {
      const { postId } = req.params;
      const commentData = insertPostCommentSchema.parse({ ...req.body, postId, userId: req.user.id });

      const comment = await storage.commentOnPost(commentData);
      res.json(comment);
    } catch (error) {
      res.status(400).json({ message: "Failed to add comment" });
    }
  });

  app.post("/api/posts/:postId/save", requireAuth, async (req, res) => {
    try {
      const { postId } = req.params;
      const userId = req.user.id; // Use authenticated user ID

      const save = await storage.savePost({ postId, userId });
      res.json(save);
    } catch (error) {
      res.status(400).json({ message: "Failed to save post" });
    }
  });

  app.delete("/api/posts/:postId/save", requireAuth, async (req, res) => {
    try {
      const { postId } = req.params;
      const userId = req.user.id; // Use authenticated user ID

      await storage.unsavePost(postId, userId);
      res.json({ message: "Post unsaved" });
    } catch (error) {
      res.status(400).json({ message: "Failed to unsave post" });
    }
  });

  app.get("/api/users/:userId/saved-posts", requireAuth, requireSelfAccess, async (req, res) => {
    try {
      const { userId } = req.params;
      const savedPosts = await storage.getUserSavedPosts(userId);
      res.json(savedPosts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch saved posts" });
    }
  });

  // School routes
  app.get("/api/schools", async (req, res) => {
    try {
      const schools = await storage.getSchools();
      res.json(schools);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch schools" });
    }
  });

  app.get("/api/schools/:schoolId/stats", async (req, res) => {
    try {
      const { schoolId } = req.params;
      const stats = await storage.getSchoolStats(schoolId);
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch school stats" });
    }
  });

  app.get("/api/schools/:schoolId/students", async (req, res) => {
    try {
      const { schoolId } = req.params;
      const students = await storage.getStudentsBySchool(schoolId);
      res.json(students);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch school students" });
    }
  });

  // System admin routes
  app.get("/api/system/stats", async (req, res) => {
    try {
      const stats = await storage.getSystemStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch system stats" });
    }
  });

  // Cloudinary file upload
  app.post("/api/upload", requireAuth, upload.single('file'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No file provided" });
      }

      // Upload to Cloudinary with transformations
      const result = await new Promise((resolve, reject) => {
        cloudinary.uploader.upload_stream(
          {
            folder: "lockerroom",
            transformation: [
              { width: 800, crop: "scale" },
              { quality: "auto:good" },
              { fetch_format: "auto" }
            ],
            resource_type: "auto"
          },
          (error, result) => {
            if (error) reject(error);
