import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import Sidebar from "@/components/navigation/sidebar";
import MobileNav from "@/components/navigation/mobile-nav";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Skeleton } from "@/components/ui/skeleton";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Search, Users, UserPlus, UserMinus, MapPin } from "lucide-react";
import type { StudentSearchResult } from "@shared/schema";

export default function SearchPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [debouncedQuery, setDebouncedQuery] = useState("");
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, navigate] = useLocation();

  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedQuery(searchQuery);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery]);

  const { data: searchResults, isLoading } = useQuery<StudentSearchResult[]>({
    queryKey: ["/api/search/students", debouncedQuery, user?.id],
    enabled: debouncedQuery.length >= 2,
    queryFn: async () => {
      const params = new URLSearchParams({
        q: debouncedQuery,
        ...(user?.id && { userId: user.id })
      });
      const response = await fetch(`/api/search/students?${params}`);
      if (!response.ok) throw new Error('Search failed');
      return response.json();
    },
  });

  const followMutation = useMutation({
    mutationFn: async ({ studentId, action }: { studentId: string; action: 'follow' | 'unfollow' }) => {
      if (action === 'follow') {
        return apiRequest("POST", `/api/students/${studentId}/follow`, { userId: user?.id });
      } else {
        return apiRequest("DELETE", `/api/students/${studentId}/follow`, { userId: user?.id });
      }
    },
    onSuccess: (_, variables) => {
      toast({
        title: variables.action === 'follow' ? 'Following' : 'Unfollowed',
        description: variables.action === 'follow' 
          ? 'You are now following this student athlete!'
          : 'You have unfollowed this student athlete.',
      });

      // Invalidate search results to update follow status
      queryClient.invalidateQueries({ queryKey: ["/api/search/students"] });
    },
    onError: (error: any) => {
      toast({
        title: "Action Failed",
        description: error.message || "Something went wrong. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleFollow = (studentId: string, isCurrentlyFollowing: boolean) => {
    if (!user) {
      toast({
        title: "Login Required",
        description: "Please log in to follow student athletes.",
        variant: "destructive",
      });
      return;
    }

    followMutation.mutate({
      studentId,
      action: isCurrentlyFollowing ? 'unfollow' : 'follow'
    });
  };

  return (
    <div className="min-h-screen bg-background">
      <Sidebar />

      {/* Main Content */}
      <div className="lg:pl-64 flex flex-col flex-1">
        {/* Mobile Header */}
        <div className="lg:hidden bg-card border-b border-border px-4 py-4">
          <div className="flex items-center">
            <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
              <span className="text-primary-foreground font-bold">LR</span>
            </div>
            <span className="ml-2 text-lg font-bold text-foreground">Search Athletes</span>
          </div>
        </div>

        {/* Search Content */}
        <main className="flex-1 pb-20 lg:pb-0">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            {/* Search Header */}
            <div className="mb-8">
              <div className="flex items-center mb-4">
                <Search className="w-8 h-8 text-primary mr-3" />
                <div>
                  <h1 className="text-3xl font-bold text-foreground">Search Athletes</h1>
                  <p className="text-muted-foreground">Find and follow student athletes</p>
                </div>
              </div>

              {/* Search Input */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  type="text"
                  placeholder="Search by name, sport, or position..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 py-3 text-lg"
                  data-testid="search-input"
                />
              </div>
            </div>

            {/* Search Results */}
            {debouncedQuery.length >= 2 && (
              <div className="space-y-6">
                {isLoading ? (
                  <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                    {[...Array(6)].map((_, i) => (
                      <Card key={i} className="animate-pulse">
                        <CardContent className="p-6">
                          <div className="flex items-start space-x-3 mb-4">
                            <Skeleton className="w-12 h-12 rounded-full" />
                            <div className="space-y-2 flex-1">
                              <Skeleton className="h-4 w-3/4" />
                              <Skeleton className="h-3 w-1/2" />
                            </div>
                          </div>
                          <div className="space-y-2 mb-4">
                            <div className="flex space-x-2">
                              <Skeleton className="h-6 w-16" />
                              <Skeleton className="h-6 w-20" />
                            </div>
                            <Skeleton className="h-3 w-2/3" />
                          </div>
                          <Skeleton className="h-3 w-full mb-2" />
                          <Skeleton className="h-3 w-4/5 mb-4" />
                          <Skeleton className="h-9 w-full" />
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                ) : searchResults && searchResults.length > 0 ? (
                  <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                    {searchResults.map((student) => (
                      <Card 
                        key={student.id} 
                        className="hover:shadow-lg transition-shadow cursor-pointer group" 
                        data-testid={`search-result-${student.id}`}
                        onClick={() => navigate(`/profile/${student.user.id}`)}
                      >
                        <CardContent className="p-6">
                            {/* Profile Picture and Basic Info */}
                            <div className="flex items-start justify-between mb-4">
                              <div className="flex items-center space-x-3">
                                <Avatar className="w-12 h-12">
                                  <AvatarImage 
                                    src={student.profilePicUrl || student.profilePic || ""} 
                                    alt={student.user.name} 
                                  />
                                  <AvatarFallback className="bg-accent/20 text-accent font-semibold">
                                    {student.user.name?.slice(0, 2).toUpperCase() || "S"}
                                  </AvatarFallback>
                                </Avatar>
                                <div>
                                  <h3 className="font-semibold text-foreground group-hover:text-primary transition-colors">{student.user.name}</h3>
                                  <div className="flex items-center text-sm text-muted-foreground">
                                    <Users className="w-3 h-3 mr-1" />
                                    {student.followersCount} followers
                                  </div>
                                </div>
                              </div>
                            </div>

                          {/* Sports Info */}
                          <div className="space-y-2 mb-4">
                            {student.sport && (
                              <div className="flex items-center space-x-2">
                                <Badge variant="secondary">{student.sport}</Badge>
                                {student.position && (
                                  <Badge variant="outline">{student.position}</Badge>
                                )}
                              </div>
                            )}

                            {student.school && (
                              <div className="flex items-center text-sm text-muted-foreground">
                                <MapPin className="w-3 h-3 mr-1" />
                                {student.school.name}
                              </div>
                            )}
                          </div>

                          {/* Bio Preview */}
                          {student.bio && (
                            <p className="text-sm text-muted-foreground mb-4 line-clamp-2">
                              {student.bio}
                            </p>
                          )}

                            {/* Follow Button */}
                            <Button
                              onClick={(e) => {
                                e.stopPropagation();
                                handleFollow(student.id, student.isFollowing || false);
                              }}
                              disabled={followMutation.isPending}
                              variant={student.isFollowing ? "outline" : "default"}
                              className={`w-full ${!student.isFollowing ? 'bg-accent hover:bg-accent/90 text-accent-foreground' : ''}`}
                              data-testid={`follow-button-${student.id}`}
                            >
                              {followMutation.isPending ? (
                                <div className="flex items-center">
                                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current mr-2"></div>
                                  Processing...
                                </div>
                              ) : student.isFollowing ? (
                                <>
                                  <UserMinus className="w-4 h-4 mr-2" />
                                  Unfollow
                                </>
                              ) : (
                                <>
                                  <UserPlus className="w-4 h-4 mr-2" />
                                  Follow
                                </>
                              )}
                            </Button>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <Search className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-foreground mb-2">No athletes found</h3>
                    <p className="text-muted-foreground">
                      Try searching with different keywords like sport or school name
                    </p>
                  </div>
                )}
              </div>
            )}

            {/* Empty State */}
            {debouncedQuery.length < 2 && (
              <div className="text-center py-12">
                <Search className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-medium text-foreground mb-2">Start searching</h3>
                <p className="text-muted-foreground">
                  Enter at least 2 characters to search for student athletes
                </p>
              </div>
            )}
          </div>
        </main>
      </div>

      <MobileNav />
    </div>
  );
}

client/src/pages/signup.tsx.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest } from "@/lib/queryClient";
import { Eye, EyeOff, Users, Trophy, Camera } from "lucide-react";

const signupSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(8, "Password must be at least 8 characters"),
  confirmPassword: z.string()
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type SignupForm = z.infer<typeof signupSchema>;

export default function Signup() {
  const [, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const { updateUser } = useAuth();
  const { toast } = useToast();

  const form = useForm<SignupForm>({
    resolver: zodResolver(signupSchema),
    defaultValues: {
      name: "",
      email: "",
      password: "",
      confirmPassword: "",
    },
  });

  const signupMutation = useMutation({
    mutationFn: async (data: Omit<SignupForm, "confirmPassword">) => {
      return apiRequest("POST", "/api/auth/signup", data);
    },
    onSuccess: (data) => {
      updateUser(data.user);
      toast({
        title: "Welcome to LockerRoom!",
        description: data.message || "Account created successfully!",
      });
      setLocation("/feed");
    },
    onError: (error: any) => {
      toast({
        title: "Signup Failed",
        description: error.message || "Something went wrong. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: SignupForm) => {
    const { confirmPassword, ...signupData } = data;
    signupMutation.mutate(signupData);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-background to-muted flex items-center justify-center p-4">
      <div className="w-full max-w-md space-y-8">
        {/* Logo and Header */}
        <div className="text-center">
          <div className="w-16 h-16 bg-primary rounded-2xl flex items-center justify-center mx-auto mb-4">
            <span className="text-2xl font-bold text-primary-foreground">LR</span>
          </div>
          <h1 className="text-3xl font-bold tracking-tight text-foreground">Join LockerRoom</h1>
          <p className="text-muted-foreground mt-2">
            Follow your favorite student athletes and stay updated with their journey
          </p>
        </div>

        {/* Features Preview */}
        <div className="grid grid-cols-3 gap-4 py-6">
          <div className="text-center">
            <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center mx-auto mb-2">
              <Users className="w-6 h-6 text-primary" />
            </div>
            <p className="text-sm text-muted-foreground">Follow Athletes</p>
          </div>
          <div className="text-center">
            <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center mx-auto mb-2">
              <Camera className="w-6 h-6 text-primary" />
            </div>
            <p className="text-sm text-muted-foreground">View Content</p>
          </div>
          <div className="text-center">
            <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center mx-auto mb-2">
              <Trophy className="w-6 h-6 text-primary" />
            </div>
            <p className="text-sm text-muted-foreground">Support Teams</p>
          </div>
        </div>

        {/* Signup Form */}
        <Card>
          <CardHeader className="space-y-1">
            <CardTitle className="text-2xl">Create your account</CardTitle>
            <CardDescription>
              Enter your information to get started
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Full Name</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="Enter your full name" 
                          data-testid="input-name"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input 
                          type="email" 
                          placeholder="Enter your email" 
                          data-testid="input-email"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input 
                            type={showPassword ? "text" : "password"}
                            placeholder="Create a password"
                            data-testid="input-password"
                            {...field} 
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                            onClick={() => setShowPassword(!showPassword)}
                            data-testid="toggle-password"
                          >
                            {showPassword ? (
                              <EyeOff className="h-4 w-4 text-muted-foreground" />
                            ) : (
                              <Eye className="h-4 w-4 text-muted-foreground" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Confirm Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input 
                            type={showConfirmPassword ? "text" : "password"}
                            placeholder="Confirm your password"
                            data-testid="input-confirm-password"
                            {...field} 
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                            onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                            data-testid="toggle-confirm-password"
                          >
                            {showConfirmPassword ? (
                              <EyeOff className="h-4 w-4 text-muted-foreground" />
                            ) : (
                              <Eye className="h-4 w-4 text-muted-foreground" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  className="w-full bg-accent hover:bg-accent/90 text-accent-foreground"
                  disabled={signupMutation.isPending}
                  data-testid="button-signup"
                >
                  {signupMutation.isPending ? "Creating Account..." : "Create Account"}
                </Button>
              </form>
            </Form>

            <div className="mt-6 text-center">
              <p className="text-sm text-muted-foreground">
                Already have an account?{" "}
                <Link href="/login">
                  <span className="text-primary hover:underline cursor-pointer" data-testid="link-login">
                    Sign in
                  </span>
                </Link>
              </p>
            </div>
          </CardContent>
        </Card>

        {/* Footer */}
        <div className="text-center text-xs text-muted-foreground">
          <p>By creating an account, you agree to follow community guidelines</p>
          <p className="mt-1">and respect student athlete privacy</p>
        </div>
      </div>
    </div>
  );
}

client/src/components/navigation/mobile-.content:
import { Link, useLocation } from "wouter";
import { Home, Search, Plus, BarChart3, User, LogOut, Settings, Bookmark, Users } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { logout } from "@/lib/auth";

export default function MobileNav() {
  const [location, setLocation] = useLocation();
  const { user, updateUser } = useAuth();

  const handleLogout = () => {
    logout();
    updateUser(null);
    setLocation("/login");
  };

  const getNavigation = () => {
    if (user?.role === "student") {
      return [
        { name: "Feed", href: "/feed", icon: Home, active: location === "/feed" },
        { name: "Search", href: "/search", icon: Search, active: location === "/search" },
        { name: "Create", href: "/create", icon: Plus, active: location === "/create" },
        { name: "Stats", href: "/stats", icon: BarChart3, active: location === "/stats" },
        { name: "Settings", href: "/settings", icon: Settings, active: location === "/settings" },
      ];
    } else if (user?.role === "viewer") {
      return [
        { name: "Feed", href: "/feed", icon: Home, active: location === "/feed" },
        { name: "Search", href: "/search", icon: Search, active: location === "/search" },
        { name: "Saved", href: "/saved", icon: Bookmark, active: location === "/saved" },
        { name: "Following", href: "/following", icon: Users, active: location === "/following" },
      ];
    } else {
      return [
        { name: "Feed", href: "/feed", icon: Home, active: location === "/feed" },
        { name: "Search", href: "/search", icon: Search, active: location === "/search" },
        { name: "Stats", href: "/stats", icon: BarChart3, active: location === "/stats" },
        { name: "Settings", href: "/settings", icon: Settings, active: location === "/settings" },
      ];
    }
  };

  const navigation = getNavigation();

  return (
    <div className="mobile-nav lg:hidden bg-card border-t border-border">
      <div className={`grid py-2 ${navigation.length === 4 ? 'grid-cols-4' : 'grid-cols-5'}`}>
        {navigation.map((item) => (
          <Link key={item.name} href={item.href}>
            <div
              className={`flex flex-col items-center py-2 px-1 transition-colors cursor-pointer ${
                item.active ? "text-accent" : "text-muted-foreground"
              }`}
              data-testid={`mobile-nav-${item.name.toLowerCase()}`}
            >
              {item.name === "Profile" ? (
                <div className="w-6 h-6 rounded-full bg-muted flex items-center justify-center">
                  <img
                    className="w-5 h-5 rounded-full"
                    src="https://images.unsplash.com/photo-1438761681033-6461ffad8d80?auto=format&fit=crop&w=400&h=400"
                    alt="Profile"
                  />
                </div>
              ) : (
                <item.icon className="w-6 h-6" />
              )}
              <span className="text-xs mt-1">{item.name}</span>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

client/src/components/navigation/sidebar.content:
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/use-auth";
import { logout } from "@/lib/auth";
import { Button } from "@/components/ui/button";
import { ThemeToggle } from "@/components/ui/theme-toggle";
import { Home, User, BarChart3, Settings, LogOut, Bookmark, Users } from "lucide-react";

export default function Sidebar() {
  const [location, setLocation] = useLocation();
  const { user, updateUser } = useAuth();

  const handleLogout = () => {
    logout();
    updateUser(null);
    setLocation("/login");
  };

  const getNavigation = () => {
    const baseNav = [
      { name: "Feed", href: "/feed", icon: Home, active: location === "/feed" },
    ];

    if (user?.role === "student") {
      return [
        ...baseNav,
        { name: "Profile", href: "/profile", icon: User, active: location === "/profile" },
        { name: "Stats", href: "/stats", icon: BarChart3, active: location === "/stats" },
        { name: "Settings", href: "/settings", icon: Settings, active: location === "/settings" },
      ];
    } else if (user?.role === "viewer") {
      return [
        ...baseNav,
        { name: "Saved", href: "/saved", icon: Bookmark, active: location === "/saved" },
        { name: "Following", href: "/following", icon: Users, active: location === "/following" },
        { name: "Settings", href: "/settings", icon: Settings, active: location === "/settings" },
      ];
    } else {
      return [
        ...baseNav,
        { name: "Stats", href: "/stats", icon: BarChart3, active: location === "/stats" },
        { name: "Settings", href: "/settings", icon: Settings, active: location === "/settings" },
      ];
    }
  };

  const navigation = getNavigation();

  if (!user) return null;

  return (
    <div className="hidden lg:fixed lg:inset-y-0 lg:flex lg:w-64 lg:flex-col">
      <div className="flex min-h-0 flex-1 flex-col bg-card border-r border-border">
        <div className="flex flex-1 flex-col pt-5 pb-4 overflow-y-auto">
          {/* Logo */}
          <div className="flex items-center flex-shrink-0 px-4">
            <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
              <span className="text-primary-foreground font-bold text-lg">LR</span>
            </div>
            <span className="ml-3 text-xl font-bold text-foreground">LockerRoom</span>
          </div>

          {/* Navigation */}
          <nav className="mt-8 flex-1 px-4 space-y-2">
            {navigation.map((item) => (
              <Link key={item.name} href={item.href}>
                <a
                  className={`group flex items-center px-4 py-3 text-sm font-medium rounded-lg transition-colors ${
                    item.active
                      ? "bg-accent/20 text-accent-foreground"
                      : "text-muted-foreground hover:bg-muted hover:text-foreground"
                  }`}
                  data-testid={`nav-${item.name.toLowerCase()}`}
                >
                  <item.icon className="w-5 h-5 mr-3" />
                  {item.name}
                </a>
              </Link>
            ))}
          </nav>

          {/* Theme Toggle and Logout */}
          <div className="px-4 py-4 space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium text-foreground">Theme</span>
              <ThemeToggle />
            </div>
            <Button
              onClick={handleLogout}
              variant="outline"
              className="w-full justify-start"
              data-testid="button-logout"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </Button>
          </div>
        </div>

        {/* User Profile */}
        <div className="flex-shrink-0 flex bg-muted p-4">
          <div className="flex items-center w-full">
            <img
              className="inline-block h-10 w-10 rounded-full"
              src="https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=400"
              alt="Profile"
            />
            <div className="ml-3 min-w-0 flex-1">
              <p className="text-sm font-medium text-foreground truncate">{user.name}</p>
              <p className="text-xs font-medium text-muted-foreground capitalize">
                {user.role.replace("_", " ")}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

