import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, TrendingUp, Users, Building2, FileText, Calendar, Activity } from "lucide-react";
import { useLocation } from "wouter";
import { LineChart, Line, AreaChart, Area, BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";

type AnalyticsStats = {
  userSignups: number;
  postCreated: number;
  schoolOnboarded: number;
  totalEvents: number;
};

type SystemStats = {
  totalSchools: number;
  activeStudents: number;
  contentUploads: number;
  monthlyRevenue: number;
  premiumSchools: number;
  standardSchools: number;
};

type AnalyticsLog = {
  id: string;
  eventType: string;
  entityId?: string;
  entityType?: string;
  metadata?: string;
  timestamp: string;
};

// Mock data for charts (in a real app, this would come from analytics)
const growthData = [
  { month: 'Jan', users: 120, schools: 5, posts: 450 },
  { month: 'Feb', users: 180, schools: 8, posts: 680 },
  { month: 'Mar', users: 250, schools: 12, posts: 920 },
  { month: 'Apr', users: 320, schools: 15, posts: 1200 },
  { month: 'May', users: 420, schools: 18, posts: 1580 },
  { month: 'Jun', users: 520, schools: 22, posts: 2100 },
];

const engagementData = [
  { day: 'Mon', likes: 320, comments: 180, saves: 90 },
  { day: 'Tue', likes: 280, comments: 150, saves: 75 },
  { day: 'Wed', likes: 450, comments: 220, saves: 110 },
  { day: 'Thu', likes: 380, comments: 190, saves: 95 },
  { day: 'Fri', likes: 520, comments: 280, saves: 140 },
  { day: 'Sat', likes: 680, comments: 350, saves: 175 },
  { day: 'Sun', likes: 590, comments: 300, saves: 150 },
];

const COLORS = ['#FFD700', '#FFA500', '#FF6B6B', '#4ECDC4', '#45B7D1'];

export default function PlatformAnalytics() {
  const [, setLocation] = useLocation();

  const { data: systemStats, isLoading: statsLoading } = useQuery<SystemStats>({
    queryKey: ["/api/system/stats"],
  });

  const { data: analyticsStats, isLoading: analyticsLoading } = useQuery<AnalyticsStats>({
    queryKey: ["/api/analytics/stats"],
  });

  const { data: recentLogs, isLoading: logsLoading } = useQuery<AnalyticsLog[]>({
    queryKey: ["/api/analytics/logs"],
    select: (data) => data?.slice(0, 10) || [],
  });

  const isLoading = statsLoading || analyticsLoading || logsLoading;

  const pieData = [
    { name: 'Premium Schools', value: systemStats?.premiumSchools || 0 },
    { name: 'Standard Schools', value: systemStats?.standardSchools || 0 },
  ];

  const getEventIcon = (eventType: string) => {
    switch (eventType) {
      case 'user_signup':
        return <Users className="w-4 h-4 text-blue-500" />;
      case 'post_created':
        return <FileText className="w-4 h-4 text-green-500" />;
      case 'school_onboarded':
        return <Building2 className="w-4 h-4 text-purple-500" />;
      default:
        return <Activity className="w-4 h-4 text-gray-500" />;
    }
  };

  const getEventDescription = (log: AnalyticsLog) => {
    switch (log.eventType) {
      case 'user_signup':
        return 'New user registered';
      case 'post_created':
        return 'New post uploaded';
      case 'school_onboarded':
        return 'School approved and onboarded';
      default:
        return log.eventType.replace('_', ' ');
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-accent"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setLocation("/system-admin")}
                className="mr-4"
                data-testid="back-to-admin"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Admin
              </Button>
              <div>
                <h1 className="text-xl font-semibold text-foreground">Platform Analytics</h1>
                <p className="text-sm text-muted-foreground">Real-time insights and performance metrics</p>
              </div>
            </div>

            <div className="flex items-center space-x-2 bg-muted px-3 py-2 rounded-lg">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span className="text-sm text-foreground">Live Data</span>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Overview Stats */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card data-testid="stat-total-events">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Events</CardTitle>
              <Activity className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{analyticsStats?.totalEvents || 0}</div>
              <p className="text-xs text-muted-foreground">All tracked activities</p>
            </CardContent>
          </Card>

          <Card data-testid="stat-user-signups">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">User Signups</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{analyticsStats?.userSignups || 0}</div>
              <p className="text-xs text-muted-foreground">New registrations</p>
            </CardContent>
          </Card>

          <Card data-testid="stat-posts-created">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Posts Created</CardTitle>
              <FileText className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{analyticsStats?.postCreated || 0}</div>
              <p className="text-xs text-muted-foreground">Content uploads</p>
            </CardContent>
          </Card>

          <Card data-testid="stat-schools-onboarded">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Schools Onboarded</CardTitle>
              <Building2 className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{analyticsStats?.schoolOnboarded || 0}</div>
              <p className="text-xs text-muted-foreground">Approved schools</p>
            </CardContent>
          </Card>
        </div>

        {/* Charts Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Growth Trends */}
          <Card data-testid="chart-growth-trends">
            <CardHeader>
              <CardTitle className="flex items-center">
                <TrendingUp className="w-5 h-5 mr-2 text-accent" />
                Growth Trends
              </CardTitle>
              <CardDescription>Platform growth over the last 6 months</CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={growthData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="users" stroke="#FFD700" strokeWidth={2} />
                  <Line type="monotone" dataKey="schools" stroke="#FFA500" strokeWidth={2} />
                  <Line type="monotone" dataKey="posts" stroke="#FF6B6B" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          {/* School Distribution */}
          <Card data-testid="chart-school-distribution">
            <CardHeader>
              <CardTitle>School Plan Distribution</CardTitle>
              <CardDescription>Breakdown of subscription plans</CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={pieData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, value, percent }) => `${name}: ${value} (${(percent * 100).toFixed(0)}%)`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {pieData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          {/* Engagement Analytics */}
          <Card data-testid="chart-engagement">
            <CardHeader>
              <CardTitle>Weekly Engagement</CardTitle>
              <CardDescription>User interactions across the week</CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <AreaChart data={engagementData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="day" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Area type="monotone" dataKey="likes" stackId="1" stroke="#FFD700" fill="#FFD700" />
                  <Area type="monotone" dataKey="comments" stackId="1" stroke="#FFA500" fill="#FFA500" />
                  <Area type="monotone" dataKey="saves" stackId="1" stroke="#FF6B6B" fill="#FF6B6B" />
                </AreaChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          {/* Revenue Analytics */}
          <Card data-testid="chart-revenue">
            <CardHeader>
              <CardTitle>Revenue Insights</CardTitle>
              <CardDescription>Monthly recurring revenue breakdown</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-muted-foreground">Monthly Revenue</span>
                  <span className="text-2xl font-bold text-accent">${systemStats?.monthlyRevenue?.toLocaleString() || 0}</span>
                </div>

                <div className="space-y-2">
                  <div className="flex justify-between items-center">
                    <span className="text-sm">Premium Schools ({systemStats?.premiumSchools || 0})</span>
                    <span className="font-medium">${((systemStats?.premiumSchools || 0) * 150).toLocaleString()}/mo</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm">Standard Schools ({systemStats?.standardSchools || 0})</span>
                    <span className="font-medium">${((systemStats?.standardSchools || 0) * 75).toLocaleString()}/mo</span>
                  </div>
                </div>

                <div className="pt-4 border-t">
                  <div className="grid grid-cols-2 gap-4 text-center">
                    <div>
                      <div className="text-lg font-bold text-green-600">
                        ${((systemStats?.monthlyRevenue || 0) * 12).toLocaleString()}
                      </div>
                      <div className="text-xs text-muted-foreground">Annual Revenue</div>
                    </div>
                    <div>
                      <div className="text-lg font-bold text-blue-600">
                        ${Math.round((systemStats?.monthlyRevenue || 0) / (systemStats?.totalSchools || 1))}
                      </div>
                      <div className="text-xs text-muted-foreground">ARPU</div>
                    </div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Recent Activity */}
        <Card data-testid="recent-activity">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Activity className="w-5 h-5 mr-2 text-accent" />
              Recent Activity
            </CardTitle>
            <CardDescription>Latest platform events and activities</CardDescription>
          </CardHeader>
          <CardContent>
            {recentLogs && recentLogs.length > 0 ? (
              <div className="space-y-4">
                {recentLogs.map((log) => (
                  <div key={log.id} className="flex items-center justify-between py-3 border-b border-border last:border-0">
                    <div className="flex items-center space-x-3">
                      {getEventIcon(log.eventType)}
                      <div>
                        <p className="text-sm font-medium">{getEventDescription(log)}</p>
                        <p className="text-xs text-muted-foreground">
                          {log.entityType && log.entityId && `${log.entityType}: ${log.entityId.substring(0, 8)}...`}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <Badge variant="outline" className="text-xs">
                        {log.eventType.replace('_', ' ')}
                      </Badge>
                      <p className="text-xs text-muted-foreground mt-1">
                        <Calendar className="w-3 h-3 inline mr-1" />
                        {new Date(log.timestamp).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <Activity className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-medium text-foreground mb-2">No Recent Activity</h3>
                <p className="text-muted-foreground">Analytics events will appear here as they occur.</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin/school-applicatio.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Building2, Calendar, MapPin, Users, Check, X, Plus } from "lucide-react";
import { useLocation } from "wouter";

type SchoolApplication = {
  id: string;
  schoolName: string;
  contactEmail: string;
  contactName: string;
  phone?: string;
  address?: string;
  city?: string;
  state?: string;
  zipCode?: string;
  expectedStudents?: number;
  planType: string;
  status: string;
  notes?: string;
  reviewedBy?: string;
  reviewedAt?: string;
  createdAt: string;
};

const addSchoolFormSchema = z.object({
  schoolName: z.string().min(1, "School name is required"),
  contactEmail: z.string().email("Valid email is required"),
  contactName: z.string().min(1, "Contact name is required"),
  phone: z.string().optional(),
  address: z.string().optional(),
  city: z.string().optional(),
  state: z.string().optional(),
  zipCode: z.string().optional(),
  expectedStudents: z.number().min(1).max(10000).default(100),
  planType: z.enum(["standard", "premium"]).default("standard"),
});

type AddSchoolFormData = z.infer<typeof addSchoolFormSchema>;

export default function SchoolApplications() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [rejectNotes, setRejectNotes] = useState("");
  const [showAddSchool, setShowAddSchool] = useState(false);

  const addSchoolForm = useForm<AddSchoolFormData>({
    resolver: zodResolver(addSchoolFormSchema),
    defaultValues: {
      expectedStudents: 100,
      planType: "standard",
    },
  });

  const { data: applications, isLoading } = useQuery<SchoolApplication[]>({
    queryKey: ["/api/admin/school-applications"],
  });

  const approveMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest(`/api/admin/school-applications/${id}/approve`, {
        method: "POST",
        body: { reviewerId: user?.id },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/school-applications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/system/stats"] });
      toast({
        title: "Application Approved",
        description: "School application has been approved successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to approve application.",
        variant: "destructive",
      });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: string; notes: string }) => {
      return apiRequest(`/api/admin/school-applications/${id}/reject`, {
        method: "POST",
        body: { reviewerId: user?.id, notes },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/school-applications"] });
      setRejectNotes("");
      toast({
        title: "Application Rejected",
        description: "School application has been rejected.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to reject application.",
        variant: "destructive",
      });
    },
  });

  const addSchoolMutation = useMutation({
    mutationFn: async (data: AddSchoolFormData) => {
      return apiRequest("/api/admin/school-applications", {
        method: "POST",
        body: data,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/school-applications"] });
      addSchoolForm.reset();
      setShowAddSchool(false);
      toast({
        title: "School Application Created",
        description: "New school application has been submitted for review.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create school application.",
        variant: "destructive",
      });
    },
  });

  const onAddSchool = (data: AddSchoolFormData) => {
    addSchoolMutation.mutate(data);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "pending":
        return <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">Pending Review</Badge>;
      case "approved":
        return <Badge variant="default" className="bg-green-100 text-green-800">Approved</Badge>;
      case "rejected":
        return <Badge variant="destructive">Rejected</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-accent"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setLocation("/system-admin")}
                className="mr-4"
                data-testid="back-to-admin"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Admin
              </Button>
              <div>
                <h1 className="text-xl font-semibold text-foreground">School Applications</h1>
                <p className="text-sm text-muted-foreground">Review and manage school registration requests</p>
              </div>
            </div>

            <Dialog open={showAddSchool} onOpenChange={setShowAddSchool}>
              <DialogTrigger asChild>
                <Button className="gold-gradient text-accent-foreground" data-testid="button-add-school">
                  <Plus className="w-4 h-4 mr-2" />
                  Add New School
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[600px]">
                <DialogHeader>
                  <DialogTitle>Add New School</DialogTitle>
                  <DialogDescription>
                    Create a new school application that will be reviewed and approved.
                  </DialogDescription>
                </DialogHeader>
                <Form {...addSchoolForm}>
                  <form onSubmit={addSchoolForm.handleSubmit(onAddSchool)} className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <FormField
                        control={addSchoolForm.control}
                        name="schoolName"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>School Name</FormLabel>
                            <FormControl>
                              <Input placeholder="Elite Soccer Academy" {...field} data-testid="input-school-name" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSchoolForm.control}
                        name="contactName"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Contact Name</FormLabel>
                            <FormControl>
                              <Input placeholder="John Smith" {...field} data-testid="input-contact-name" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <FormField
                        control={addSchoolForm.control}
                        name="contactEmail"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Contact Email</FormLabel>
                            <FormControl>
                              <Input type="email" placeholder="admin@school.edu" {...field} data-testid="input-contact-email" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSchoolForm.control}
                        name="phone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Phone (Optional)</FormLabel>
                            <FormControl>
                              <Input placeholder="(555) 123-4567" {...field} data-testid="input-phone" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <FormField
                        control={addSchoolForm.control}
                        name="expectedStudents"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Expected Students</FormLabel>
                            <FormControl>
                              <Input
                                type="number"
                                placeholder="100"
                                {...field}
                                onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                                data-testid="input-expected-students"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSchoolForm.control}
                        name="planType"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Plan Type</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-plan-type">
                                  <SelectValue placeholder="Select plan" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="standard">Standard ($75/month)</SelectItem>
                                <SelectItem value="premium">Premium ($150/month)</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <DialogFooter>
                      <Button 
                        type="submit" 
                        disabled={addSchoolMutation.isPending}
                        data-testid="button-submit-school"
                      >
                        {addSchoolMutation.isPending ? "Creating..." : "Create School Application"}
                      </Button>
                    </DialogFooter>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
          </div>
        </div>
      </div>

      {/* Applications List */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {applications && applications.length > 0 ? (
          <div className="space-y-6">
            {applications.map((application) => (
              <Card key={application.id} className="overflow-hidden" data-testid={`application-${application.id}`}>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-accent/20 rounded-lg flex items-center justify-center">
                        <Building2 className="w-6 h-6 text-accent" />
                      </div>
                      <div>
                        <CardTitle className="text-lg">{application.schoolName}</CardTitle>
                        <CardDescription>{application.contactName} â€¢ {application.contactEmail}</CardDescription>
                      </div>
                    </div>
                    <div className="flex items-center space-x-3">
                      {getStatusBadge(application.status)}
                      <Badge variant="outline">{application.planType}</Badge>
                    </div>
                  </div>
                </CardHeader>

                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div className="space-y-4">
                      <div className="flex items-center text-sm text-muted-foreground">
                        <Users className="w-4 h-4 mr-2" />
                        {application.expectedStudents || 'N/A'} Expected Students
                      </div>
                      {application.phone && (
                        <div className="flex items-center text-sm text-muted-foreground">
                          <MapPin className="w-4 h-4 mr-2" />
                          {application.phone}
                        </div>
                      )}
                      <div className="flex items-center text-sm text-muted-foreground">
                        <Calendar className="w-4 h-4 mr-2" />
                        Applied {new Date(application.createdAt).toLocaleDateString()}
                      </div>
                    </div>

                    {application.notes && (
                      <div className="md:col-span-2">
                        <h4 className="font-medium text-sm mb-2">Review Notes</h4>
                        <p className="text-sm text-muted-foreground bg-muted p-3 rounded-lg">
                          {application.notes}
                        </p>
                      </div>
                    )}
                  </div>

                  {application.status === "pending" && (
                    <div className="flex items-center justify-end space-x-3 mt-6 pt-6 border-t border-border">
                      <Dialog>
                        <DialogTrigger asChild>
                          <Button variant="outline" size="sm" data-testid={`button-reject-${application.id}`}>
                            <X className="w-4 h-4 mr-2" />
                            Reject
                          </Button>
                        </DialogTrigger>
                        <DialogContent>
                          <DialogHeader>
                            <DialogTitle>Reject Application</DialogTitle>
                            <DialogDescription>
                              Please provide a reason for rejecting {application.schoolName}'s application.
                            </DialogDescription>
                          </DialogHeader>
                          <div className="space-y-4">
                            <div>
                              <Label htmlFor="reject-notes">Rejection Notes</Label>
                              <Textarea
                                id="reject-notes"
                                value={rejectNotes}
                                onChange={(e) => setRejectNotes(e.target.value)}
                                placeholder="Please explain why this application is being rejected..."
                                className="mt-1"
                                data-testid="textarea-reject-notes"
                              />
                            </div>
                          </div>
                          <DialogFooter>
                            <Button
                              onClick={() => rejectMutation.mutate({ id: application.id, notes: rejectNotes })}
                              disabled={rejectMutation.isPending}
                              variant="destructive"
                              data-testid="button-confirm-reject"
                            >
                              {rejectMutation.isPending ? "Rejecting..." : "Reject Application"}
                            </Button>
                          </DialogFooter>
                        </DialogContent>
                      </Dialog>

                      <Button
                        onClick={() => approveMutation.mutate(application.id)}
                        disabled={approveMutation.isPending}
                        className="gold-gradient text-accent-foreground"
                        size="sm"
                        data-testid={`button-approve-${application.id}`}
                      >
                        <Check className="w-4 h-4 mr-2" />
                        {approveMutation.isPending ? "Approving..." : "Approve"}
                      </Button>
                    </div>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <Building2 className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-medium text-foreground mb-2">No School Applications</h3>
            <p className="text-muted-foreground mb-6">
              There are currently no school applications to review.
            </p>
            <Button 
              onClick={() => setShowAddSchool(true)}
              className="gold-gradient text-accent-foreground"
              data-testid="button-add-first-school"
            >
              <Plus className="w-4 h-4 mr-2" />
              Add New School
            </Button>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin/system-config.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Settings, Palette, ToggleLeft, ToggleRight, Plus, Trash2, Save, Mail } from "lucide-react";
import { useLocation } from "wouter";

type SystemSetting = {
  id: string;
  key: string;
  value: string;
  category: string;
  description?: string;
  updatedBy: string;
  updatedAt: string;
};

const addSettingFormSchema = z.object({
  key: z.string().min(1, "Key is required").regex(/^[a-zA-Z_][a-zA-Z0-9_]*$/, "Key must be valid identifier"),
  value: z.string().min(1, "Value is required"),
  category: z.enum(["general", "theme", "features", "email"]),
  description: z.string().optional(),
});

type AddSettingFormData = z.infer<typeof addSettingFormSchema>;

const predefinedSettings = {
  theme: [
    { key: "primary_color", value: "#FFD700", description: "Primary brand color (XEN Gold)" },
    { key: "secondary_color", value: "#000000", description: "Secondary color (Black)" },
    { key: "accent_color", value: "#FFFFFF", description: "Accent color (White)" },
    { key: "dark_mode_enabled", value: "true", description: "Enable dark mode support" },
  ],
  features: [
    { key: "public_signup_enabled", value: "true", description: "Allow public user registration" },
    { key: "media_upload_enabled", value: "true", description: "Enable media uploads" },
    { key: "comments_enabled", value: "true", description: "Enable post comments" },
    { key: "follow_system_enabled", value: "true", description: "Enable follow/unfollow system" },
    { key: "search_enabled", value: "true", description: "Enable student search" },
  ],
  email: [
    { key: "welcome_email_enabled", value: "true", description: "Send welcome emails to new users" },
    { key: "notification_email_enabled", value: "true", description: "Send notification emails" },
    { key: "admin_email", value: "admin@lockerroom.com", description: "Administrator contact email" },
  ],
  general: [
    { key: "platform_name", value: "LockerRoom", description: "Platform display name" },
    { key: "max_file_size_mb", value: "50", description: "Maximum file upload size in MB" },
    { key: "session_timeout_hours", value: "24", description: "User session timeout in hours" },
  ],
};

export default function SystemConfig() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showAddSetting, setShowAddSetting] = useState(false);
  const [activeCategory, setActiveCategory] = useState<string>("general");

  const addSettingForm = useForm<AddSettingFormData>({
    resolver: zodResolver(addSettingFormSchema),
    defaultValues: {
      category: "general",
    },
  });

  const { data: settings, isLoading } = useQuery<SystemSetting[]>({
    queryKey: ["/api/admin/system-settings"],
  });

  const updateSettingMutation = useMutation({
    mutationFn: async ({ key, value }: { key: string; value: string }) => {
      return apiRequest("/api/admin/system-settings", {
        method: "POST",
        body: {
          key,
          value,
          category: settings?.find(s => s.key === key)?.category || "general",
          updatedBy: user?.id,
        },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/system-settings"] });
      toast({
        title: "Setting Updated",
        description: "System setting has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update system setting.",
        variant: "destructive",
      });
    },
  });

  const addSettingMutation = useMutation({
    mutationFn: async (data: AddSettingFormData) => {
      return apiRequest("/api/admin/system-settings", {
        method: "POST",
        body: {
          ...data,
          updatedBy: user?.id,
        },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/system-settings"] });
      addSettingForm.reset();
      setShowAddSetting(false);
      toast({
        title: "Setting Added",
        description: "New system setting has been created.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create system setting.",
        variant: "destructive",
      });
    },
  });

  const deleteSettingMutation = useMutation({
    mutationFn: async (key: string) => {
      return apiRequest(`/api/admin/system-settings/${key}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/system-settings"] });
      toast({
        title: "Setting Deleted",
        description: "System setting has been removed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete system setting.",
        variant: "destructive",
      });
    },
  });

  const initializeDefaultSettings = async () => {
    const allSettings = Object.values(predefinedSettings).flat();
    for (const setting of allSettings) {
      try {
        await apiRequest("/api/admin/system-settings", {
          method: "POST",
          body: {
            ...setting,
            category: Object.entries(predefinedSettings).find(([_, settings]) => 
              settings.some(s => s.key === setting.key)
            )?.[0] || "general",
            updatedBy: user?.id,
          },
        });
      } catch (error) {
        console.log(`Setting ${setting.key} might already exist`);
      }
    }
    queryClient.invalidateQueries({ queryKey: ["/api/admin/system-settings"] });
    toast({
      title: "Default Settings Initialized",
      description: "Platform has been configured with default settings.",
    });
  };

  const onAddSetting = (data: AddSettingFormData) => {
    addSettingMutation.mutate(data);
  };

  const getSetting = (key: string) => {
    return settings?.find(s => s.key === key);
  };

  const getSettingValue = (key: string, defaultValue = "") => {
    return getSetting(key)?.value || defaultValue;
  };

  const updateSetting = (key: string, value: string) => {
    updateSettingMutation.mutate({ key, value });
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case "theme":
        return <Palette className="w-5 h-5" />;
      case "features":
        return <ToggleLeft className="w-5 h-5" />;
      case "email":
        return <Mail className="w-5 h-5" />;
      default:
        return <Settings className="w-5 h-5" />;
    }
  };

  const filteredSettings = settings?.filter(s => s.category === activeCategory) || [];
  const categories = ["general", "theme", "features", "email"];

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-accent"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setLocation("/system-admin")}
                className="mr-4"
                data-testid="back-to-admin"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Admin
              </Button>
              <div>
                <h1 className="text-xl font-semibold text-foreground">System Configuration</h1>
                <p className="text-sm text-muted-foreground">Manage platform-wide settings and preferences</p>
              </div>
            </div>

            <div className="flex items-center space-x-3">
              {(!settings || settings.length === 0) && (
                <Button
                  onClick={initializeDefaultSettings}
                  variant="outline"
                  data-testid="button-initialize-defaults"
                >
                  Initialize Defaults
                </Button>
              )}
              <Dialog open={showAddSetting} onOpenChange={setShowAddSetting}>
                <DialogTrigger asChild>
                  <Button className="gold-gradient text-accent-foreground" data-testid="button-add-setting">
                    <Plus className="w-4 h-4 mr-2" />
                    Add Setting
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Add System Setting</DialogTitle>
                    <DialogDescription>
                      Create a new platform-wide configuration setting.
                    </DialogDescription>
                  </DialogHeader>
                  <Form {...addSettingForm}>
                    <form onSubmit={addSettingForm.handleSubmit(onAddSetting)} className="space-y-4">
                      <FormField
                        control={addSettingForm.control}
                        name="key"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Setting Key</FormLabel>
                            <FormControl>
                              <Input placeholder="setting_name" {...field} data-testid="input-setting-key" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSettingForm.control}
                        name="value"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Value</FormLabel>
                            <FormControl>
                              <Input placeholder="Setting value" {...field} data-testid="input-setting-value" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSettingForm.control}
                        name="category"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Category</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-setting-category">
                                  <SelectValue placeholder="Select category" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="general">General</SelectItem>
                                <SelectItem value="theme">Theme</SelectItem>
                                <SelectItem value="features">Features</SelectItem>
                                <SelectItem value="email">Email</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSettingForm.control}
                        name="description"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Description (Optional)</FormLabel>
                            <FormControl>
                              <Textarea 
                                placeholder="Describe what this setting controls..." 
                                {...field} 
                                data-testid="textarea-setting-description"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <DialogFooter>
                        <Button 
                          type="submit" 
                          disabled={addSettingMutation.isPending}
                          data-testid="button-submit-setting"
                        >
                          {addSettingMutation.isPending ? "Adding..." : "Add Setting"}
                        </Button>
                      </DialogFooter>
                    </form>
                  </Form>
                </DialogContent>
              </Dialog>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Category Sidebar */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Categories</CardTitle>
                <CardDescription>Select a settings category</CardDescription>
              </CardHeader>
              <CardContent className="p-0">
                <div className="space-y-1">
                  {categories.map((category) => (
                    <button
                      key={category}
                      onClick={() => setActiveCategory(category)}
                      className={`w-full flex items-center space-x-3 px-4 py-3 text-left transition-colors hover:bg-muted ${
                        activeCategory === category ? "bg-muted border-r-2 border-accent" : ""
                      }`}
                      data-testid={`category-${category}`}
                    >
                      {getCategoryIcon(category)}
                      <span className="font-medium capitalize">{category}</span>
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Settings Content */}
          <div className="lg:col-span-3">
            <Card>
              <CardHeader>
                <div className="flex items-center space-x-3">
                  {getCategoryIcon(activeCategory)}
                  <div>
                    <CardTitle className="capitalize">{activeCategory} Settings</CardTitle>
                    <CardDescription>
                      Configure {activeCategory} settings for the platform
                    </CardDescription>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                {filteredSettings.length > 0 ? (
                  <div className="space-y-6">
                    {filteredSettings.map((setting) => (
                      <div key={setting.id} className="border border-border rounded-lg p-4" data-testid={`setting-${setting.key}`}>
                        <div className="flex items-start justify-between">
                          <div className="flex-1 mr-4">
                            <div className="flex items-center space-x-2 mb-2">
                              <Label className="font-medium">{setting.key.replace(/_/g, ' ')}</Label>
                              <code className="px-2 py-1 bg-muted text-xs rounded">{setting.key}</code>
                            </div>
                            {setting.description && (
                              <p className="text-sm text-muted-foreground mb-3">{setting.description}</p>
                            )}

                            {/* Dynamic input based on value type */}
                            {setting.value === "true" || setting.value === "false" ? (
                              <div className="flex items-center space-x-2">
                                <Switch
                                  checked={setting.value === "true"}
                                  onCheckedChange={(checked) => updateSetting(setting.key, checked.toString())}
                                  data-testid={`switch-${setting.key}`}
                                />
                                <span className="text-sm">{setting.value === "true" ? "Enabled" : "Disabled"}</span>
                              </div>
                            ) : setting.key.includes("color") ? (
                              <div className="flex items-center space-x-2">
                                <Input
                                  type="color"
                                  value={setting.value}
                                  onChange={(e) => updateSetting(setting.key, e.target.value)}
                                  className="w-16 h-10"
                                  data-testid={`color-${setting.key}`}
                                />
                                <Input
                                  value={setting.value}
                                  onChange={(e) => updateSetting(setting.key, e.target.value)}
                                  placeholder="Color value"
                                  className="flex-1"
                                  data-testid={`input-${setting.key}`}
                                />
                              </div>
                            ) : (
                              <Input
                                value={setting.value}
                                onChange={(e) => updateSetting(setting.key, e.target.value)}
                                placeholder="Setting value"
                                data-testid={`input-${setting.key}`}
                              />
                            )}

                            <div className="flex items-center justify-between mt-2 text-xs text-muted-foreground">
                              <span>Last updated: {new Date(setting.updatedAt).toLocaleDateString()}</span>
                            </div>
                          </div>

                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => deleteSettingMutation.mutate(setting.key)}
                            disabled={deleteSettingMutation.isPending}
                            data-testid={`button-delete-${setting.key}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <Settings className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-foreground mb-2">No {activeCategory} Settings</h3>
                    <p className="text-muted-foreground mb-6">
                      No settings found in the {activeCategory} category.
                    </p>
                    <Button 
                      onClick={() => setShowAddSetting(true)}
                      className="gold-gradient text-accent-foreground"
                      data-testid="button-add-first-setting"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Add First Setting
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/school-admin/add-studen.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, Upload, User, Save } from "lucide-react";
import { useLocation } from "wouter";

const addStudentFormSchema = z.object({
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Valid email is required"),
  phone: z.string().optional(),
  gender: z.enum(["male", "female", "other"]).optional(),
  dateOfBirth: z.string().optional(),
  grade: z.string().min(1, "Grade/Class is required"),
  guardianContact: z.string().optional(),
  roleNumber: z.string().optional(),
  position: z.string().optional(),
  sport: z.string().optional(),
  bio: z.string().optional(),
});

type AddStudentFormData = z.infer<typeof addStudentFormSchema>;

export default function AddStudent() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [profilePicFile, setProfilePicFile] = useState<File | null>(null);
  const [profilePicPreview, setProfilePicPreview] = useState<string | null>(null);

  const form = useForm<AddStudentFormData>({
    resolver: zodResolver(addStudentFormSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      grade: "",
      guardianContact: "",
      roleNumber: "",
      position: "",
      sport: "",
      bio: "",
    },
  });

  const addStudentMutation = useMutation({
    mutationFn: async (data: AddStudentFormData) => {
      const formData = new FormData();

      // Add form fields
      Object.entries(data).forEach(([key, value]) => {
        if (value) formData.append(key, value);
      });

      // Add profile picture if selected
      if (profilePicFile) {
        formData.append("profilePic", profilePicFile);
      }

      const response = await fetch(`/api/schools/${user?.schoolId}/students`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create student");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId, "students"] });
      queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId, "stats"] });
      toast({
        title: "Student Added",
        description: "Student has been successfully registered.",
      });
      setLocation("/school-admin");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to add student.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: AddStudentFormData) => {
    addStudentMutation.mutate(data);
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Validate file type and size
      if (!file.type.startsWith("image/")) {
        toast({
          title: "Invalid File",
          description: "Please select an image file.",
          variant: "destructive",
        });
        return;
      }

      if (file.size > 5 * 1024 * 1024) { // 5MB limit
        toast({
          title: "File too large",
          description: `Please select an image smaller than 5MB. Current file: ${(file.size / (1024 * 1024)).toFixed(1)}MB`,
          variant: "destructive",
        });
        return;
      }

      // Show success message
      toast({
        title: "Profile image selected",
        description: `Image ready for upload (${(file.size / (1024 * 1024)).toFixed(1)}MB)`,
      });

      setProfilePicFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = () => {
        setProfilePicPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeProfilePic = () => {
    setProfilePicFile(null);
    setProfilePicPreview(null);
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setLocation("/school-admin")}
                className="mr-4"
                data-testid="back-to-admin"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Admin
              </Button>
              <div>
                <h1 className="text-xl font-semibold text-foreground">Add New Student</h1>
                <p className="text-sm text-muted-foreground">Register a new student in your school</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <User className="w-5 h-5 mr-2 text-accent" />
              Student Registration
            </CardTitle>
            <CardDescription>
              Fill in the student's information below. Fields marked with * are required.
            </CardDescription>
          </CardHeader>

          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                {/* Profile Picture Upload */}
                <div className="space-y-4">
                  <Label>Profile Picture</Label>
                  <div className="flex items-center space-x-4">
                    {profilePicPreview ? (
                      <div className="relative">
                        <img
                          src={profilePicPreview}
                          alt="Profile preview"
                          className="w-24 h-24 rounded-full object-cover border-2 border-border"
                          data-testid="profile-pic-preview"
                        />
                        <Button
                          type="button"
                          variant="destructive"
                          size="sm"
                          className="absolute -top-2 -right-2 rounded-full w-6 h-6 p-0"
                          onClick={removeProfilePic}
                          data-testid="remove-profile-pic"
                        >
                          Ã—
                        </Button>
                      </div>
                    ) : (
                      <div className="w-24 h-24 rounded-full bg-muted border-2 border-dashed border-border flex items-center justify-center">
                        <User className="w-8 h-8 text-muted-foreground" />
                      </div>
                    )}

                    <div>
                      <input
                        type="file"
                        accept="image/*"
                        onChange={handleFileChange}
                        className="hidden"
                        id="profile-pic-input"
                        data-testid="profile-pic-input"
                      />
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => document.getElementById("profile-pic-input")?.click()}
                        data-testid="upload-profile-pic"
                      >
                        <Upload className="w-4 h-4 mr-2" />
                        Upload Photo
                      </Button>
                      <p className="text-xs text-muted-foreground mt-1">
                        Max 5MB, JPG or PNG
                      </p>
                    </div>
                  </div>
                </div>

                {/* Basic Information */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Full Name *</FormLabel>
                        <FormControl>
                          <Input placeholder="John Smith" {...field} data-testid="input-name" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email Address *</FormLabel>
                        <FormControl>
                          <Input 
                            type="email" 
                            placeholder="john@example.com" 
                            {...field} 
                            data-testid="input-email" 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="phone"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Phone Number</FormLabel>
                        <FormControl>
                          <Input placeholder="(555) 123-4567" {...field} data-testid="input-phone" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="dateOfBirth"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Date of Birth</FormLabel>
                        <FormControl>
                          <Input type="date" {...field} data-testid="input-dob" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="gender"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Gender</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-gender">
                              <SelectValue placeholder="Select gender" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="male">Male</SelectItem>
                            <SelectItem value="female">Female</SelectItem>
                            <SelectItem value="other">Other</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="grade"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Grade/Class *</FormLabel>
                        <FormControl>
                          <Input placeholder="Grade 10, Class A" {...field} data-testid="input-grade" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Guardian Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-medium">Guardian Information</h3>
                  <FormField
                    control={form.control}
                    name="guardianContact"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Guardian Contact</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="Parent name and phone number" 
                            {...field} 
                            data-testid="input-guardian" 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Sports Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-medium">Sports Information (Optional)</h3>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <FormField
                      control={form.control}
                      name="sport"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Sport</FormLabel>
                          <FormControl>
                            <Input placeholder="Soccer, Basketball, etc." {...field} data-testid="input-sport" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="position"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Position</FormLabel>
                          <FormControl>
                            <Input placeholder="Forward, Midfielder, etc." {...field} data-testid="input-position" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="roleNumber"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Jersey Number</FormLabel>
                          <FormControl>
                            <Input placeholder="7, 10, etc." {...field} data-testid="input-jersey" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>
                </div>

                {/* Bio */}
                <FormField
                  control={form.control}
                  name="bio"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Bio (Optional)</FormLabel>
                      <FormControl>
                        <Textarea 
                          placeholder="Brief description about the student..." 
                          className="min-h-[100px]"
                          {...field} 
                          data-testid="input-bio"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Submit Button */}
                <div className="flex items-center justify-end space-x-4 pt-6 border-t border-border">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setLocation("/school-admin")}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    disabled={addStudentMutation.isPending}
                    className="gold-gradient text-accent-foreground min-w-32"
                    data-testid="button-submit"
                  >
                    {addStudentMutation.isPending ? (
                      "Adding..."
                    ) : (
                      <>
                        <Save className="w-4 h-4 mr-2" />
                        Add Student
                      </>
                    )}
                  </Button>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/school-admin/manage-set.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Settings, School, Users, Plus, Trash2, Save } from "lucide-react";
import { useLocation } from "wouter";

type SchoolSetting = {
  id: string;
  schoolId: string;
  key: string;
  value: string;
  category: string;
  updatedBy: string;
  updatedAt: string;
};

type School = {
  id: string;
  name: string;
  subscriptionPlan: string;
  maxStudents: number;
  createdAt: string;
};

const addSettingFormSchema = z.object({
  key: z.string().min(1, "Key is required").regex(/^[a-zA-Z_][a-zA-Z0-9_]*$/, "Key must be valid identifier"),
  value: z.string().min(1, "Value is required"),
  category: z.enum(["general", "grades", "staff"]),
});

type AddSettingFormData = z.infer<typeof addSettingFormSchema>;

const predefinedSettings = {
  general: [
    { key: "school_logo_url", value: "", description: "URL to school logo image" },
    { key: "school_address", value: "", description: "Physical address of the school" },
    { key: "school_phone", value: "", description: "Main school contact number" },
    { key: "school_email", value: "", description: "Official school email address" },
    { key: "academic_year", value: "2024-2025", description: "Current academic year" },
  ],
  grades: [
    { key: "grade_levels", value: "9,10,11,12", description: "Available grade levels (comma-separated)" },
    { key: "class_size_limit", value: "30", description: "Maximum students per class" },
    { key: "grading_scale", value: "A,B,C,D,F", description: "Grading system used" },
    { key: "semester_system", value: "true", description: "Use semester-based system" },
  ],
  staff: [
    { key: "max_teachers", value: "50", description: "Maximum number of teachers" },
    { key: "teacher_student_ratio", value: "1:20", description: "Preferred teacher-to-student ratio" },
    { key: "admin_approval_required", value: "true", description: "Require admin approval for new staff" },
  ],
};

export default function ManageSettings() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showAddSetting, setShowAddSetting] = useState(false);
  const [activeCategory, setActiveCategory] = useState<string>("general");

  const addSettingForm = useForm<AddSettingFormData>({
    resolver: zodResolver(addSettingFormSchema),
    defaultValues: {
      category: "general",
    },
  });

  const { data: school, isLoading: schoolLoading } = useQuery<School>({
    queryKey: ["/api/schools", user?.schoolId],
    enabled: !!user?.schoolId,
  });

  const { data: settings, isLoading: settingsLoading } = useQuery<SchoolSetting[]>({
    queryKey: ["/api/schools", user?.schoolId, "settings"],
    enabled: !!user?.schoolId,
  });

  const updateSchoolMutation = useMutation({
    mutationFn: async (updates: Partial<School>) => {
      return apiRequest(`/api/schools/${user?.schoolId}`, {
        method: "PUT",
        body: updates,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId] });
      toast({
        title: "School Updated",
        description: "School information has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update school information.",
        variant: "destructive",
      });
    },
  });

  const updateSettingMutation = useMutation({
    mutationFn: async ({ key, value }: { key: string; value: string }) => {
      return apiRequest(`/api/schools/${user?.schoolId}/settings`, {
        method: "POST",
        body: {
          key,
          value,
          category: settings?.find(s => s.key === key)?.category || "general",
          updatedBy: user?.id,
        },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId, "settings"] });
      toast({
        title: "Setting Updated",
        description: "School setting has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update school setting.",
        variant: "destructive",
      });
    },
  });

  const addSettingMutation = useMutation({
    mutationFn: async (data: AddSettingFormData) => {
      return apiRequest(`/api/schools/${user?.schoolId}/settings`, {
        method: "POST",
        body: {
          ...data,
          updatedBy: user?.id,
        },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId, "settings"] });
      addSettingForm.reset();
      setShowAddSetting(false);
      toast({
        title: "Setting Added",
        description: "New school setting has been created.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create school setting.",
        variant: "destructive",
      });
    },
  });

  const deleteSettingMutation = useMutation({
    mutationFn: async (key: string) => {
      return apiRequest(`/api/schools/${user?.schoolId}/settings/${key}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId, "settings"] });
      toast({
        title: "Setting Deleted",
        description: "School setting has been removed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete school setting.",
        variant: "destructive",
      });
    },
  });

  const initializeDefaultSettings = async () => {
    const allSettings = Object.values(predefinedSettings).flat();
    for (const setting of allSettings) {
      try {
        await apiRequest(`/api/schools/${user?.schoolId}/settings`, {
          method: "POST",
          body: {
            ...setting,
            category: Object.entries(predefinedSettings).find(([_, settings]) => 
              settings.some(s => s.key === setting.key)
            )?.[0] || "general",
            updatedBy: user?.id,
          },
        });
      } catch (error) {
        console.log(`Setting ${setting.key} might already exist`);
      }
    }
    queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId, "settings"] });
    toast({
      title: "Default Settings Initialized",
      description: "School has been configured with default settings.",
    });
  };

  const onAddSetting = (data: AddSettingFormData) => {
    addSettingMutation.mutate(data);
  };

  const getSetting = (key: string) => {
    return settings?.find(s => s.key === key);
  };

  const getSettingValue = (key: string, defaultValue = "") => {
    return getSetting(key)?.value || defaultValue;
  };

  const updateSetting = (key: string, value: string) => {
    updateSettingMutation.mutate({ key, value });
  };

  const updateSchoolInfo = (field: string, value: string | number) => {
    updateSchoolMutation.mutate({ [field]: value });
  };

  const filteredSettings = settings?.filter(s => s.category === activeCategory) || [];
  const categories = ["general", "grades", "staff"];

  const isLoading = schoolLoading || settingsLoading;

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-accent"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setLocation("/school-admin")}
                className="mr-4"
                data-testid="back-to-admin"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Admin
              </Button>
              <div>
                <h1 className="text-xl font-semibold text-foreground">Manage Settings</h1>
                <p className="text-sm text-muted-foreground">Configure your school's information and preferences</p>
              </div>
            </div>

            <div className="flex items-center space-x-3">
              {(!settings || settings.length === 0) && (
                <Button
                  disabled
                  variant="outline"
                  className="cursor-not-allowed"
                  data-testid="button-initialize-defaults"
                >
                  Initialize Defaults
                </Button>
              )}
              <Dialog open={showAddSetting} onOpenChange={setShowAddSetting}>
                <DialogTrigger asChild>
                  <Button disabled className="cursor-not-allowed" data-testid="button-add-setting">
                    <Plus className="w-4 h-4 mr-2" />
                    Add Setting
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Add School Setting</DialogTitle>
                    <DialogDescription>
                      Create a new configuration setting for your school.
                    </DialogDescription>
                  </DialogHeader>
                  <Form {...addSettingForm}>
                    <form onSubmit={addSettingForm.handleSubmit(onAddSetting)} className="space-y-4">
                      <FormField
                        control={addSettingForm.control}
                        name="key"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Setting Key</FormLabel>
                            <FormControl>
                              <Input placeholder="setting_name" {...field} data-testid="input-setting-key" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSettingForm.control}
                        name="value"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Value</FormLabel>
                            <FormControl>
                              <Input placeholder="Setting value" {...field} data-testid="input-setting-value" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={addSettingForm.control}
                        name="category"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Category</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-setting-category">
                                  <SelectValue placeholder="Select category" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="general">General</SelectItem>
                                <SelectItem value="grades">Grades</SelectItem>
                                <SelectItem value="staff">Staff</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <DialogFooter>
                        <Button 
                          type="submit" 
                          disabled={addSettingMutation.isPending}
                          data-testid="button-submit-setting"
                        >
                          {addSettingMutation.isPending ? "Adding..." : "Add Setting"}
                        </Button>
                      </DialogFooter>
                    </form>
                  </Form>
                </DialogContent>
              </Dialog>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Navigation Sidebar */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Settings Categories</CardTitle>
                <CardDescription>Select a category to configure</CardDescription>
              </CardHeader>
              <CardContent className="p-0">
                <div className="space-y-1">
                  {categories.map((category) => (
                    <button
                      key={category}
                      onClick={() => setActiveCategory(category)}
                      className={`w-full flex items-center space-x-3 px-4 py-3 text-left transition-colors hover:bg-muted ${
                        activeCategory === category ? "bg-muted border-r-2 border-accent" : ""
                      }`}
                      data-testid={`category-${category}`}
                    >
                      {category === "general" && <School className="w-5 h-5" />}
                      {category === "grades" && <Settings className="w-5 h-5" />}
                      {category === "staff" && <Users className="w-5 h-5" />}
                      <span className="font-medium capitalize">{category}</span>
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3 space-y-8">
            {/* School Information Card */}
            {activeCategory === "general" && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <School className="w-5 h-5 mr-2 text-accent" />
                    School Information
                  </CardTitle>
                  <CardDescription>View your school's basic information (read-only)</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <Label>School Name</Label>
                        <Input
                          value={school?.name || ""}
                          readOnly
                          className="bg-muted cursor-not-allowed"
                          placeholder="Elite Soccer Academy"
                          data-testid="input-school-name"
                        />
                      </div>

                      <div>
                        <Label>Subscription Plan</Label>
                        <Input
                          value={school?.subscriptionPlan === "premium" ? "Premium ($150/month)" : "Standard ($75/month)"}
                          readOnly
                          className="bg-muted cursor-not-allowed"
                          data-testid="select-subscription-plan"
                        />
                      </div>
                    </div>

                    <div>
                      <Label>Maximum Students</Label>
                      <Input
                        type="number"
                        value={school?.maxStudents || 100}
                        readOnly
                        className="bg-muted cursor-not-allowed"
                        placeholder="100"
                        data-testid="input-max-students"
                      />
                    </div>

                    <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg p-4">
                      <div className="flex items-center">
                        <div className="flex-shrink-0">
                          <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                          </svg>
                        </div>
                        <div className="ml-3">
                          <p className="text-sm text-yellow-800 dark:text-yellow-200">
                            School information can only be modified by system administrators. Contact support if changes are needed.
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Settings Configuration */}
            <Card>
              <CardHeader>
                <CardTitle className="capitalize">{activeCategory} Settings</CardTitle>
                <CardDescription>
                  View {activeCategory} settings for your school (read-only)
                </CardDescription>
              </CardHeader>
              <CardContent>
                {filteredSettings.length > 0 ? (
                  <div className="space-y-6">
                    {filteredSettings.map((setting) => (
                      <div key={setting.id} className="border border-border rounded-lg p-4 bg-muted/50" data-testid={`setting-${setting.key}`}>
                        <div className="flex items-start justify-between">
                          <div className="flex-1 mr-4">
                            <div className="flex items-center space-x-2 mb-2">
                              <Label className="font-medium">{setting.key.replace(/_/g, ' ')}</Label>
                              <code className="px-2 py-1 bg-muted text-xs rounded">{setting.key}</code>
                            </div>

                            {/* Dynamic input based on value type and key */}
                            {setting.value === "true" || setting.value === "false" ? (
                              <div className="flex items-center space-x-2">
                                <Switch
                                  checked={setting.value === "true"}
                                  disabled
                                  data-testid={`switch-${setting.key}`}
                                />
                                <span className="text-sm">{setting.value === "true" ? "Enabled" : "Disabled"}</span>
                              </div>
                            ) : setting.key.includes("url") ? (
                              <Input
                                type="url"
                                value={setting.value}
                                readOnly
                                className="bg-muted cursor-not-allowed"
                                placeholder="https://example.com/image.jpg"
                                data-testid={`input-${setting.key}`}
                              />
                            ) : setting.key.includes("email") ? (
                              <Input
                                type="email"
                                value={setting.value}
                                readOnly
                                className="bg-muted cursor-not-allowed"
                                placeholder="admin@school.edu"
                                data-testid={`input-${setting.key}`}
                              />
                            ) : setting.key.includes("phone") ? (
                              <Input
                                type="tel"
                                value={setting.value}
                                readOnly
                                className="bg-muted cursor-not-allowed"
                                placeholder="(555) 123-4567"
                                data-testid={`input-${setting.key}`}
                              />
                            ) : setting.key.includes("address") ? (
                              <Textarea
                                value={setting.value}
                                readOnly
                                className="bg-muted cursor-not-allowed"
                                placeholder="123 School St, City, State 12345"
                                rows={2}
                                data-testid={`textarea-${setting.key}`}
                              />
                            ) : (
                              <Input
                                value={setting.value}
                                readOnly
                                className="bg-muted cursor-not-allowed"
                                placeholder="Setting value"
                                data-testid={`input-${setting.key}`}
                              />
                            )}

                            <div className="flex items-center justify-between mt-2 text-xs text-muted-foreground">
                              <span>Last updated: {new Date(setting.updatedAt).toLocaleDateString()}</span>
                            </div>
                          </div>

                          <Button
                            variant="outline"
                            size="sm"
                            disabled
                            className="cursor-not-allowed"
                            data-testid={`button-delete-${setting.key}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    ))}

                    <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg p-4 mt-6">
                      <div className="flex items-center">
                        <div className="flex-shrink-0">
                          <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                          </svg>
                        </div>
                        <div className="ml-3">
                          <p className="text-sm text-yellow-800 dark:text-yellow-200">
                            School settings can only be modified by system administrators. Contact support if changes are needed.
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <Settings className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-foreground mb-2">No {activeCategory} Settings</h3>
                    <p className="text-muted-foreground mb-6">
                      No settings found in the {activeCategory} category. Contact system administrators to add settings.
                    </p>
                    <Button 
                      disabled
                      className="cursor-not-allowed"
                      data-testid="button-add-first-setting"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Add First Setting
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/school-admin/student-se.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Search, User, Star, Plus, Edit, Trash2, Mail, Phone } from "lucide-react";
import { useLocation } from "wouter";

type Student = {
  id: string;
  userId: string;
  schoolId: string;
  name: string;
  email: string;
  phone?: string;
  gender?: string;
  dateOfBirth?: string;
  grade?: string;
  guardianContact?: string;
  profilePicUrl?: string;
  roleNumber?: string;
  position?: string;
  sport?: string;
  bio?: string;
  createdAt: string;
};

type StudentRating = {
  id: string;
  studentId: string;
  rating: number;
  comments?: string;
  category: string;
  ratedBy: string;
  createdAt: string;
};

const addRatingFormSchema = z.object({
  rating: z.number().min(1).max(5),
  comments: z.string().optional(),
  category: z.enum(["overall", "academic", "athletic", "behavior"]).default("overall"),
});

type AddRatingFormData = z.infer<typeof addRatingFormSchema>;

export default function StudentSearch() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedStudent, setSelectedStudent] = useState<Student | null>(null);
  const [showAddRating, setShowAddRating] = useState(false);

  const ratingForm = useForm<AddRatingFormData>({
    resolver: zodResolver(addRatingFormSchema),
    defaultValues: {
      rating: 5,
      category: "overall",
    },
  });

  // Fetch all students when no search query
  const { data: allStudents, isLoading: allStudentsLoading } = useQuery<Student[]>({
    queryKey: ["/api/schools", user?.schoolId, "students"],
    enabled: !!user?.schoolId && !searchQuery,
  });

  // Search students when there's a query
  const { data: searchResults, isLoading: searchLoading } = useQuery<Student[]>({
    queryKey: ["/api/schools", user?.schoolId, "students/search", searchQuery],
    queryFn: async () => {
      const response = await fetch(`/api/schools/${user?.schoolId}/students/search?q=${encodeURIComponent(searchQuery)}`);
      if (!response.ok) throw new Error("Search failed");
      return response.json();
    },
    enabled: !!user?.schoolId && searchQuery.length > 0,
  });

  // Fetch ratings for selected student
  const { data: ratingsData, isLoading: ratingsLoading } = useQuery<{
    ratings: StudentRating[];
    averageRating: number;
  }>({
    queryKey: ["/api/students", selectedStudent?.id, "ratings"],
    enabled: !!selectedStudent,
  });

  const addRatingMutation = useMutation({
    mutationFn: async (data: AddRatingFormData) => {
      return apiRequest(`/api/students/${selectedStudent?.id}/ratings`, {
        method: "POST",
        body: {
          ...data,
          ratedBy: user?.id,
        },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/students", selectedStudent?.id, "ratings"] });
      queryClient.invalidateQueries({ queryKey: ["/api/schools", user?.schoolId, "analytics"] });
      ratingForm.reset();
      setShowAddRating(false);
      toast({
        title: "Rating Added",
        description: "Student rating has been added successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add student rating.",
        variant: "destructive",
      });
    },
  });

  const deleteRatingMutation = useMutation({
    mutationFn: async (ratingId: string) => {
      return apiRequest(`/api/ratings/${ratingId}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/students", selectedStudent?.id, "ratings"] });
      toast({
        title: "Rating Deleted",
        description: "Student rating has been removed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete rating.",
        variant: "destructive",
      });
    },
  });

  const onAddRating = (data: AddRatingFormData) => {
    addRatingMutation.mutate(data);
  };

  const students = searchQuery ? searchResults : allStudents;
  const isLoading = searchQuery ? searchLoading : allStudentsLoading;

  const getRatingColor = (rating: number) => {
    if (rating >= 4.5) return "bg-green-100 text-green-800 border-green-200";
    if (rating >= 3.5) return "bg-yellow-100 text-yellow-800 border-yellow-200";
    if (rating >= 2.5) return "bg-orange-100 text-orange-800 border-orange-200";
    return "bg-red-100 text-red-800 border-red-200";
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case "academic": return "ðŸŽ“";
      case "athletic": return "âš½";
      case "behavior": return "ðŸ‘";
      default: return "â­";
    }
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setLocation("/school-admin")}
                className="mr-4"
                data-testid="back-to-admin"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Admin
              </Button>
              <div>
                <h1 className="text-xl font-semibold text-foreground">Student Search & Ratings</h1>
                <p className="text-sm text-muted-foreground">Search students and manage their performance ratings</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Search & Student List */}
          <div className="lg:col-span-1 space-y-6">
            {/* Search Bar */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg flex items-center">
                  <Search className="w-5 h-5 mr-2 text-accent" />
                  Search Students
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                  <Input
                    placeholder="Search by name, email, grade, or jersey number..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10"
                    data-testid="search-input"
                  />
                </div>
              </CardContent>
            </Card>

            {/* Student List */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">
                  {searchQuery ? `Search Results (${students?.length || 0})` : `All Students (${students?.length || 0})`}
                </CardTitle>
              </CardHeader>
              <CardContent>
                {isLoading ? (
                  <div className="flex items-center justify-center py-8">
                    <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-accent"></div>
                  </div>
                ) : students && students.length > 0 ? (
                  <div className="space-y-3 max-h-96 overflow-y-auto">
                    {students.map((student) => (
                      <div
                        key={student.id}
                        onClick={() => setSelectedStudent(student)}
                        className={`flex items-center space-x-3 p-3 rounded-lg cursor-pointer transition-colors hover:bg-muted ${
                          selectedStudent?.id === student.id ? "bg-muted ring-2 ring-accent" : ""
                        }`}
                        data-testid={`student-${student.id}`}
                      >
                        <div className="w-10 h-10 bg-accent/20 rounded-full flex items-center justify-center flex-shrink-0">
                          {student.profilePicUrl ? (
                            <img
                              src={student.profilePicUrl}
                              alt={student.name}
                              className="w-10 h-10 rounded-full object-cover"
                            />
                          ) : (
                            <User className="w-5 h-5 text-accent" />
                          )}
                        </div>
                        <div className="flex-1 min-w-0">
                          <p className="text-sm font-medium truncate">{student.name}</p>
                          <p className="text-xs text-muted-foreground">
                            {student.grade && `Grade ${student.grade}`}
                            {student.sport && ` â€¢ ${student.sport}`}
                            {student.roleNumber && ` #${student.roleNumber}`}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <User className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-foreground mb-2">
                      {searchQuery ? "No results found" : "No students"}
                    </h3>
                    <p className="text-muted-foreground">
                      {searchQuery 
                        ? "Try adjusting your search terms"
                        : "No students have been registered yet"
                      }
                    </p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Student Details & Ratings */}
          <div className="lg:col-span-2">
            {selectedStudent ? (
              <div className="space-y-6">
                {/* Student Profile */}
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center justify-between">
                      <div className="flex items-center">
                        <User className="w-5 h-5 mr-2 text-accent" />
                        Student Profile
                      </div>
                      <div className="flex items-center space-x-2">
                        {ratingsData && (
                          <Badge 
                            variant="outline" 
                            className={getRatingColor(ratingsData.averageRating)}
                          >
                            <Star className="w-3 h-3 mr-1" />
                            {ratingsData.averageRating.toFixed(1)}/5
                          </Badge>
                        )}
                      </div>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="flex items-start space-x-6">
                      <div className="w-24 h-24 bg-accent/20 rounded-lg flex items-center justify-center flex-shrink-0">
                        {selectedStudent.profilePicUrl ? (
                          <img
                            src={selectedStudent.profilePicUrl}
                            alt={selectedStudent.name}
                            className="w-24 h-24 rounded-lg object-cover"
                          />
                        ) : (
                          <User className="w-12 h-12 text-accent" />
                        )}
                      </div>

                      <div className="flex-1 space-y-4">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <h3 className="text-lg font-semibold">{selectedStudent.name}</h3>
                            <div className="space-y-1 text-sm text-muted-foreground">
                              <div className="flex items-center">
                                <Mail className="w-3 h-3 mr-2" />
                                {selectedStudent.email}
                              </div>
                              {selectedStudent.phone && (
                                <div className="flex items-center">
                                  <Phone className="w-3 h-3 mr-2" />
                                  {selectedStudent.phone}
                                </div>
                              )}
                            </div>
                          </div>

                          <div className="space-y-2">
                            {selectedStudent.grade && (
                              <Badge variant="outline">Grade {selectedStudent.grade}</Badge>
                            )}
                            {selectedStudent.sport && (
                              <Badge variant="outline">{selectedStudent.sport}</Badge>
                            )}
                            {selectedStudent.position && (
                              <Badge variant="outline">{selectedStudent.position}</Badge>
                            )}
                            {selectedStudent.roleNumber && (
                              <Badge variant="outline">#{selectedStudent.roleNumber}</Badge>
                            )}
                          </div>
                        </div>

                        {selectedStudent.bio && (
                          <div>
                            <h4 className="font-medium text-sm mb-1">Bio</h4>
                            <p className="text-sm text-muted-foreground">{selectedStudent.bio}</p>
                          </div>
                        )}

                        {selectedStudent.guardianContact && (
                          <div>
                            <h4 className="font-medium text-sm mb-1">Guardian Contact</h4>
                            <p className="text-sm text-muted-foreground">{selectedStudent.guardianContact}</p>
                          </div>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>

                {/* Ratings Section */}
                <Card>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <div>
                        <CardTitle className="flex items-center">
                          <Star className="w-5 h-5 mr-2 text-accent" />
                          Performance Ratings
                        </CardTitle>
                        <CardDescription>
                          {ratingsData?.ratings.length || 0} rating(s) â€¢ Average: {ratingsData?.averageRating.toFixed(1) || '0.0'}/5
                        </CardDescription>
                      </div>

                      <Dialog open={showAddRating} onOpenChange={setShowAddRating}>
                        <DialogTrigger asChild>
                          <Button className="gold-gradient text-accent-foreground" data-testid="button-add-rating">
                            <Plus className="w-4 h-4 mr-2" />
                            Add Rating
                          </Button>
                        </DialogTrigger>
                        <DialogContent>
                          <DialogHeader>
                            <DialogTitle>Add Performance Rating</DialogTitle>
                            <DialogDescription>
                              Rate {selectedStudent.name}'s performance in different areas.
                            </DialogDescription>
                          </DialogHeader>
                          <Form {...ratingForm}>
                            <form onSubmit={ratingForm.handleSubmit(onAddRating)} className="space-y-4">
                              <FormField
                                control={ratingForm.control}
                                name="category"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Category</FormLabel>
                                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                                      <FormControl>
                                        <SelectTrigger data-testid="select-rating-category">
                                          <SelectValue placeholder="Select category" />
                                        </SelectTrigger>
                                      </FormControl>
                                      <SelectContent>
                                        <SelectItem value="overall">Overall Performance</SelectItem>
                                        <SelectItem value="academic">Academic</SelectItem>
                                        <SelectItem value="athletic">Athletic</SelectItem>
                                        <SelectItem value="behavior">Behavior</SelectItem>
                                      </SelectContent>
                                    </Select>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <FormField
                                control={ratingForm.control}
                                name="rating"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Rating (1-5 stars)</FormLabel>
                                    <Select onValueChange={(value) => field.onChange(parseInt(value))} defaultValue={field.value?.toString()}>
                                      <FormControl>
                                        <SelectTrigger data-testid="select-rating-value">
                                          <SelectValue placeholder="Select rating" />
                                        </SelectTrigger>
                                      </FormControl>
                                      <SelectContent>
                                        <SelectItem value="5">â­â­â­â­â­ Excellent (5)</SelectItem>
                                        <SelectItem value="4">â­â­â­â­ Good (4)</SelectItem>
                                        <SelectItem value="3">â­â­â­ Average (3)</SelectItem>
                                        <SelectItem value="2">â­â­ Below Average (2)</SelectItem>
                                        <SelectItem value="1">â­ Poor (1)</SelectItem>
                                      </SelectContent>
                                    </Select>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <FormField
                                control={ratingForm.control}
                                name="comments"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Comments (Optional)</FormLabel>
                                    <FormControl>
                                      <Textarea 
                                        placeholder="Additional feedback..." 
                                        {...field} 
                                        data-testid="textarea-rating-comments"
                                      />
                                    </FormControl>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <DialogFooter>
                                <Button 
                                  type="submit" 
                                  disabled={addRatingMutation.isPending}
                                  data-testid="button-submit-rating"
                                >
                                  {addRatingMutation.isPending ? "Adding..." : "Add Rating"}
                                </Button>
                              </DialogFooter>
                            </form>
                          </Form>
                        </DialogContent>
                      </Dialog>
                    </div>
                  </CardHeader>
                  <CardContent>
                    {ratingsLoading ? (
                      <div className="flex items-center justify-center py-8">
                        <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-accent"></div>
                      </div>
                    ) : ratingsData?.ratings && ratingsData.ratings.length > 0 ? (
                      <div className="space-y-4">
                        {ratingsData.ratings.map((rating) => (
                          <div
                            key={rating.id}
                            className="flex items-start justify-between p-4 bg-muted rounded-lg"
                            data-testid={`rating-${rating.id}`}
                          >
                            <div className="flex-1">
                              <div className="flex items-center space-x-2 mb-2">
                                <span className="text-lg">{getCategoryIcon(rating.category)}</span>
                                <Badge variant="outline" className={getRatingColor(rating.rating)}>
                                  {rating.rating}/5
                                </Badge>
                                <Badge variant="secondary" className="capitalize">
                                  {rating.category}
                                </Badge>
                              </div>
                              {rating.comments && (
                                <p className="text-sm text-muted-foreground mb-2">{rating.comments}</p>
                              )}
                              <p className="text-xs text-muted-foreground">
                                Added {new Date(rating.createdAt).toLocaleDateString()}
                              </p>
                            </div>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => deleteRatingMutation.mutate(rating.id)}
                              disabled={deleteRatingMutation.isPending}
                              data-testid={`delete-rating-${rating.id}`}
                            >
                              <Trash2 className="w-4 h-4" />
                            </Button>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <div className="text-center py-12">
                        <Star className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                        <h3 className="text-lg font-medium text-foreground mb-2">No Ratings Yet</h3>
                        <p className="text-muted-foreground mb-6">
                          This student hasn't been rated yet. Add their first performance rating.
                        </p>
                        <Button 
                          onClick={() => setShowAddRating(true)}
                          className="gold-gradient text-accent-foreground"
                          data-testid="button-add-first-rating"
                        >
                          <Plus className="w-4 h-4 mr-2" />
                          Add First Rating
                        </Button>
                      </div>
                    )}
                  </CardContent>
                </Card>
              </div>
            ) : (
              <div className="text-center py-20">
                <Search className="w-16 h-16 text-muted-foreground mx-auto mb-6" />
                <h3 className="text-xl font-medium text-foreground mb-4">Select a Student</h3>
                <p className="text-muted-foreground mb-8">
                  Search for a student on the left or select one from the list to view their profile and ratings.
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

