import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Link } from "wouter";
import Sidebar from "@/components/navigation/sidebar";
import MobileNav from "@/components/navigation/mobile-nav";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { Loader2, Users, UserCheck, UserX } from "lucide-react";

interface FollowingStudent {
  id: string;
  user: {
    id: string;
    name: string;
    email: string;
  };
  sport: string;
  roleNumber: string;
  position: string;
  profilePicUrl?: string;
  profilePic?: string;
  school?: {
    name: string;
  };
  followersCount: number;
}

export default function Following() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: followingStudents, isLoading, error } = useQuery<FollowingStudent[]>({
    queryKey: ["/api/users/following", user?.id],
    queryFn: async () => {
      if (!user) return [];
      const response = await fetch(`/api/users/${user.id}/following`);
      return response.json();
    },
    enabled: !!user,
  });

  const unfollowMutation = useMutation({
    mutationFn: async (studentId: string) => {
      await fetch(`/api/students/${studentId}/follow`, {
        method: "DELETE",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId: user?.id }),
      });
    },
    onSuccess: (_, studentId) => {
      queryClient.invalidateQueries({ queryKey: ["/api/users/following"] });
      const student = followingStudents?.find(s => s.id === studentId);
      toast({
        title: "Unfollowed",
        description: `You are no longer following ${student?.user.name}`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to unfollow student",
        variant: "destructive",
      });
    },
  });

  const handleUnfollow = (studentId: string) => {
    unfollowMutation.mutate(studentId);
  };

  return (
    <div className="min-h-screen bg-background">
      <Sidebar />

      {/* Main Content */}
      <div className="lg:pl-64 flex flex-col flex-1">
        {/* Header */}
        <div className="bg-card border-b border-border px-4 py-6 lg:px-8">
          <div className="max-w-2xl mx-auto">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-accent/20 rounded-xl flex items-center justify-center">
                <Users className="w-6 h-6 text-accent" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-foreground">Following</h1>
                <p className="text-muted-foreground">Student athletes you're following</p>
              </div>
            </div>
          </div>
        </div>

        {/* Content */}
        <main className="flex-1 pb-20 lg:pb-0">
          <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            {isLoading ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-8 h-8 animate-spin text-muted-foreground" />
              </div>
            ) : error ? (
              <div className="text-center py-8">
                <p className="text-destructive">Failed to load following list</p>
              </div>
            ) : followingStudents && followingStudents.length > 0 ? (
              <div>
                <p className="text-sm text-muted-foreground mb-6">
                  Following {followingStudents.length} student athlete{followingStudents.length !== 1 ? 's' : ''}
                </p>
                <div className="space-y-4">
                  {followingStudents.map((student) => (
                    <div key={student.id} className="bg-card border border-border rounded-xl p-6">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <Link href={`/profile/${student.user?.id || student.id}`}>
                            <Avatar className="h-16 w-16 cursor-pointer hover:opacity-80 transition-opacity" data-testid={`avatar-student-${student.id}`}>
                              <AvatarImage 
                                src={student.profilePicUrl || student.profilePic || ""} 
                                alt={student?.user?.name || (student as any).name} 
                              />
                              <AvatarFallback className="bg-accent/20 text-accent font-semibold">
                                {student?.user?.name?.slice(0, 2).toUpperCase() || (student as any).name?.slice(0, 2).toUpperCase() || "S"}
                              </AvatarFallback>
                            </Avatar>
                          </Link>
                          <div>
                            <h3 className="font-semibold text-foreground text-lg">{student?.user?.name || (student as any).name}</h3>
                            <p className="text-muted-foreground">
                              {student.sport} â€¢ #{student.roleNumber} â€¢ {student.position}
                            </p>
                            {student.school && (
                              <p className="text-sm text-muted-foreground">{student.school.name}</p>
                            )}
                            <p className="text-sm text-muted-foreground">
                              {student.followersCount} followers
                            </p>
                          </div>
                        </div>
                        <Button
                          onClick={() => handleUnfollow(student.id)}
                          disabled={unfollowMutation.isPending}
                          variant="outline"
                          className="bg-background hover:bg-destructive hover:text-destructive-foreground"
                          data-testid={`unfollow-${student.id}`}
                        >
                          {unfollowMutation.isPending ? (
                            <Loader2 className="w-4 h-4 animate-spin mr-2" />
                          ) : (
                            <UserX className="w-4 h-4 mr-2" />
                          )}
                          Unfollow
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ) : (
              <div className="text-center py-12">
                <div className="w-16 h-16 bg-muted rounded-xl flex items-center justify-center mx-auto mb-4">
                  <Users className="w-8 h-8 text-muted-foreground" />
                </div>
                <h3 className="text-lg font-medium text-foreground mb-2">Not following anyone yet</h3>
                <p className="text-muted-foreground max-w-sm mx-auto">
                  Search for student athletes and follow them to see their posts and updates.
                  You can search from the main feed.
                </p>
              </div>
            )}
          </div>
        </main>
      </div>

      <MobileNav />
    </div>
  );
}

client/src/pages/saved.tsx.content:
import { useQuery } from "@tanstack/react-query";
import Sidebar from "@/components/navigation/sidebar";
import MobileNav from "@/components/navigation/mobile-nav";
import PostCard from "@/components/posts/post-card";
import { useAuth } from "@/hooks/use-auth";
import { Loader2, Bookmark } from "lucide-react";
import type { PostWithDetails } from "@shared/schema";

export default function Saved() {
  const { user } = useAuth();

  const { data: savedPosts, isLoading, error } = useQuery<PostWithDetails[]>({
    queryKey: ["/api/posts/saved", user?.id],
    queryFn: async () => {
      if (!user) return [];
      const response = await fetch(`/api/users/${user.id}/saved-posts`);
      return response.json();
    },
    enabled: !!user,
  });

  return (
    <div className="min-h-screen bg-background">
      <Sidebar />

      {/* Main Content */}
      <div className="lg:pl-64 flex flex-col flex-1">
        {/* Header */}
        <div className="bg-card border-b border-border px-4 py-6 lg:px-8">
          <div className="max-w-2xl mx-auto">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-accent/20 rounded-xl flex items-center justify-center">
                <Bookmark className="w-6 h-6 text-accent" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-foreground">Saved Posts</h1>
                <p className="text-muted-foreground">Posts you've bookmarked to view later</p>
              </div>
            </div>
          </div>
        </div>

        {/* Content */}
        <main className="flex-1 pb-20 lg:pb-0">
          <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            {isLoading ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-8 h-8 animate-spin text-muted-foreground" />
              </div>
            ) : error ? (
              <div className="text-center py-8">
                <p className="text-destructive">Failed to load saved posts</p>
              </div>
            ) : savedPosts && savedPosts.length > 0 ? (
              <div>
                <p className="text-sm text-muted-foreground mb-6">
                  {savedPosts.length} saved post{savedPosts.length !== 1 ? 's' : ''}
                </p>
                {savedPosts.map((post) => (
                  <PostCard key={post.id} post={post} />
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <div className="w-16 h-16 bg-muted rounded-xl flex items-center justify-center mx-auto mb-4">
                  <Bookmark className="w-8 h-8 text-muted-foreground" />
                </div>
                <h3 className="text-lg font-medium text-foreground mb-2">No saved posts yet</h3>
                <p className="text-muted-foreground max-w-sm mx-auto">
                  When you save posts, they'll appear here so you can easily find them later.
                  Tap the bookmark icon on any post to save it.
                </p>
              </div>
            )}
          </div>
        </main>
      </div>

      <MobileNav />
    </div>
  );
}

